import React, { useState } from 'react';
import PriceModal from './PriceModal';

function ProductBlock({ product, onAddToCart, onClick }) {
  const [showModal, setShowModal] = useState(false);
  const [isHovered, setIsHovered] = useState(false);

  const handleClick = () => {
    setShowModal(true);
  };

  const handleSubmit = (price) => {
    onAddToCart(product, price);
  };

  return (
    <>
      <div 
        onClick={() => onClick(product)}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        style={{ 
          height: '120px',
          display: 'flex',
          flexDirection: 'column',
          backgroundColor: 'white',
          borderRadius: '12px',
          border: '1px solid',
          borderColor: isHovered ? 'rgb(37, 99, 235)' : 'rgb(226, 232, 240)',
          overflow: 'hidden',
          transition: 'all 0.15s ease',
          cursor: 'pointer',
          padding: '16px',
          justifyContent: 'center',
          position: 'relative'
        }}
      >
        <span style={{ 
          fontSize: '15px',
          fontWeight: '500',
          color: 'rgb(15, 23, 42)',
          letterSpacing: '-0.01em',
          textAlign: 'center'
        }}>
          {product.Product}
        </span>

        <div style={{
          position: 'absolute',
          bottom: '16px',
          left: '16px',
          right: '16px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          gap: '8px',
          opacity: isHovered ? 1 : 0,
          transform: `translateY(${isHovered ? '0' : '4px'})`,
          transition: 'all 0.15s ease'
        }}>
          <span style={{
            fontSize: '13px',
            color: 'rgb(37, 99, 235)',
            display: 'flex',
            alignItems: 'center',
            gap: '4px'
          }}>
            Set price
            <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M5 12h14M12 5l7 7-7 7"/>
            </svg>
          </span>
        </div>

        {isHovered && (
          <div style={{
            position: 'absolute',
            top: '0',
            left: '0',
            right: '0',
            height: '3px',
            background: 'linear-gradient(90deg, rgb(37, 99, 235) 0%, rgb(59, 130, 246) 100%)',
            transition: 'opacity 0.2s ease'
          }}/>
        )}
      </div>

      {showModal && (
        <PriceModal
          product={product}
          onSubmit={handleSubmit}
          onClose={() => setShowModal(false)}
        />
      )}
    </>
  );
}

export default ProductBlock;
