import React from 'react';
import { Link, useLocation } from 'react-router-dom';

function Sidebar() {
  const location = useLocation();
  const isMobile = window.innerWidth <= 768;

  return (
    <aside style={{
      width: isMobile ? '40px' : '240px', // Much smaller on mobile
      height: '100vh',
      backgroundColor: 'white',
      borderRight: '1px solid hsl(240 5.9% 90%)',
      position: 'fixed',
      left: 0,
      top: 0,
      zIndex: 50,
      display: 'flex',
      flexDirection: 'column',
      transition: 'width 0.2s ease'
    }}>
      {/* Hide header on mobile */}
      {!isMobile && (
        <div style={{
          padding: '24px',
          borderBottom: '1px solid hsl(240 5.9% 90%)',
          fontWeight: '600',
          color: 'hsl(222.2 47.4% 11.2%)',
          fontSize: '16px'
        }}>
          HGT Admin
        </div>
      )}

      <nav style={{ padding: isMobile ? '8px 0' : '16px 0' }}>
        <Link
          to="/admin/sales"
          style={{
            display: 'flex',
            alignItems: 'center',
            padding: isMobile ? '8px' : '12px 16px',
            gap: '12px',
            textDecoration: 'none',
            color: location.pathname.includes('/admin/sales') 
              ? 'hsl(222.2 47.4% 11.2%)'
              : 'hsl(215.4 16.3% 46.9%)',
            backgroundColor: location.pathname.includes('/admin/sales')
              ? 'hsl(210 40% 98%)'
              : 'transparent',
            fontSize: '14px'
          }}
        >
          <svg
            width={isMobile ? "20" : "16"}
            height={isMobile ? "20" : "16"}
            viewBox="0 0 24 24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          >
            <path d="M3 3v18h18" />
            <path d="M18.7 8l-5.1 5.2-2.8-2.7L7 14.3" />
          </svg>
          {!isMobile && <span>Sales</span>}
        </Link>

        {/* Add other menu items with the same pattern */}
        {/* Example for products */}
        <Link
          to="/admin/products"
          style={{
            display: 'flex',
            alignItems: 'center',
            padding: isMobile ? '8px' : '12px 16px',
            gap: '12px',
            textDecoration: 'none',
            color: location.pathname.includes('/admin/products')
              ? 'hsl(222.2 47.4% 11.2%)'
              : 'hsl(215.4 16.3% 46.9%)',
            backgroundColor: location.pathname.includes('/admin/products')
              ? 'hsl(210 40% 98%)'
              : 'transparent',
            fontSize: '14px'
          }}
        >
          <svg
            width={isMobile ? "20" : "16"}
            height={isMobile ? "20" : "16"}
            viewBox="0 0 24 24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          >
            <path d="M20.91 8.84L8.56 2.23a1.93 1.93 0 0 0-1.81 0L3.1 4.13a2.12 2.12 0 0 0-.05 3.69l12.22 6.93a2 2 0 0 0 1.94 0L21 12.51a2.12 2.12 0 0 0-.09-3.67Z" />
            <path d="m3.09 8.84 12.35-6.61a1.93 1.93 0 0 1 1.81 0l3.65 1.9a2.12 2.12 0 0 1 .1 3.69L8.73 14.75a2 2 0 0 1-1.94 0L3 12.51a2.12 2.12 0 0 1 .09-3.67Z" />
          </svg>
          {!isMobile && <span>Products</span>}
        </Link>
      </nav>
    </aside>
  );
}

export default Sidebar;
