import React, { useEffect, useState } from 'react';
import API from '../services/api';
import ProductBlock from './ProductBlock';
import Cart from './Cart';
import { useNavigate } from 'react-router-dom';
import SuccessModal from './SuccessModal';

const ID_MAP = {
  'Phone': 1,
  'Laptop': 3,
  'Funda': 5,
  'Charger': 7,
  'Figura': 9
};

function ProductList() {
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [priceDialogOpen, setPriceDialogOpen] = useState(false);
  const [price, setPrice] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await API.get('/api/products?populate=*');
        console.log('Raw product data from API:', response.data.data);

        // Map products to have correct IDs
        const correctedProducts = response.data.data.map(product => ({
          ...product,
          id: ID_MAP[product.Product] // Use the correct ID from our mapping
        }));

        console.log('Products with corrected IDs:', correctedProducts);
        setProducts(correctedProducts);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const addToCart = (product, price) => {
    console.log('Adding to cart:', {
      productName: product.Product,
      correctId: ID_MAP[product.Product],
      price
    });
    setCartItems([...cartItems, { product, price }]);
  };

  const removeFromCart = (index) => {
    setCartItems(cartItems.filter((_, i) => i !== index));
  };

  const handleCheckout = async () => {
    try {
      console.log('=== STARTING CHECKOUT ===');
      console.log('Cart items:', cartItems);

      for (const item of cartItems) {
        const saleData = {
          Price: parseFloat(item.price),
          Time: new Date().toISOString(),
          store: parseInt(localStorage.getItem('storeId')),
          product: item.product.id  // Make sure we're sending the product ID
        };

        console.log('=== CREATING SALE ===');
        console.log('Sale data:', saleData);

        const response = await API.post('/api/sales/create-with-relation', {
          data: saleData
        });

        console.log('=== SALE RESPONSE ===');
        console.log('Status:', response.status);
        console.log('Response data:', response.data);
      }

      setCartItems([]);
      navigate('/sales');
    } catch (error) {
      console.error('=== CHECKOUT ERROR ===');
      console.error('Error:', error);
      console.error('Response:', error.response?.data);
    }
  };

  const handleProductClick = (product) => {
    setSelectedProduct(product);
    setPriceDialogOpen(true);
  };

  const handlePriceSubmit = () => {
    if (selectedProduct && price) {
      addToCart(selectedProduct, price);
      setPriceDialogOpen(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 py-4 sm:px-6 lg:px-8 flex justify-between items-center">
          <h1 className="text-2xl font-semibold text-gray-900">Products</h1>
          <button 
            onClick={() => setDrawerOpen(true)}
            className="relative p-2 text-gray-600 hover:text-gray-900"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2.293 2.293c-.63.63-.184 1.707.707 1.707H17m0 0a2 2 0 100 4 2 2 0 000-4zm-8 2a2 2 0 11-4 0 2 2 0 014 0z" />
            </svg>
            {cartItems.length > 0 && (
              <span className="absolute -top-1 -right-1 bg-red-500 text-white rounded-full w-5 h-5 text-xs flex items-center justify-center">
                {cartItems.length}
              </span>
            )}
          </button>
        </div>
      </header>

      {/* Product Grid */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {products.map((product) => (
            <div
              key={product.id}
              onClick={() => handleProductClick(product)}
              className="bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow duration-200 cursor-pointer"
            >
              <div className="p-6">
                <h3 className="text-lg font-medium text-gray-900">{product.Product}</h3>
                <p className="mt-2 text-sm text-gray-500">Click to add to cart</p>
              </div>
            </div>
          ))}
        </div>
      </main>

      {/* Price Dialog */}
      {priceDialogOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <div className="bg-white rounded-lg max-w-md w-full p-6">
            <h2 className="text-xl font-semibold mb-4">Set Price for {selectedProduct?.Product}</h2>
            <input
              type="number"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              placeholder="Enter price"
            />
            <div className="mt-6 flex justify-end space-x-3">
              <button
                onClick={() => setPriceDialogOpen(false)}
                className="px-4 py-2 text-gray-700 hover:text-gray-900"
              >
                Cancel
              </button>
              <button
                onClick={handlePriceSubmit}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
                disabled={!price}
              >
                Add to Cart
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Cart Section - Right 50% */}
      <div style={{ 
        width: '50%',
        height: '100%'
      }}>
        <Cart 
          items={cartItems} 
          removeFromCart={removeFromCart}
          checkout={handleCheckout}
        />
      </div>

      {/* Add Success Modal */}
      {showSuccessModal && (
        <SuccessModal
          items={cartItems}
          total={cartItems.reduce((sum, item) => sum + parseFloat(item.price), 0)}
          onClose={() => {
            setShowSuccessModal(false);
            setCartItems([]); // Clear cart after modal is closed
          }}
        />
      )}
    </div>
  );
}

export default ProductList;
