import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { 
  Drawer, 
  List, 
  ListItem, 
  ListItemText,
  ListItemIcon, 
  Typography,
  Box,
  useMediaQuery
} from '@mui/material';
import DashboardIcon from '@mui/icons-material/Dashboard';
import StoreIcon from '@mui/icons-material/Store';
import BarChartIcon from '@mui/icons-material/BarChart';

function Sidebar() {
  const location = useLocation();
  const isMobile = useMediaQuery('(max-width:768px)');

  const menuItems = [
    { name: 'General', path: '/admin/sales', icon: <DashboardIcon /> },
    { name: 'Product Stats', path: '/admin/product-stats', icon: <BarChartIcon /> },
    { name: 'Gaudi', path: '/admin/sales/gaudi', icon: <StoreIcon /> },
    { name: 'Paralel', path: '/admin/sales/paralel', icon: <StoreIcon /> },
    { name: 'Mallorca', path: '/admin/sales/mallorca', icon: <StoreIcon /> },
    { name: 'Consell', path: '/admin/sales/consell', icon: <StoreIcon /> },
    { name: 'Hospital', path: '/admin/sales/hospital', icon: <StoreIcon /> }
  ];

  return (
    <Drawer
      variant="permanent"
      sx={{
        width: isMobile ? 50 : 180,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: isMobile ? 50 : 180,
          boxSizing: 'border-box',
          background: '#fff',
          borderRight: '1px solid rgba(0, 0, 0, 0.12)',
          overflowX: 'hidden'
        },
      }}
    >
      {!isMobile && (
        <Box sx={{ 
          p: 3, 
          background: '#1976d2',
          color: 'white' 
        }}>
          <Typography variant="h5" sx={{ 
            mb: 1, 
            fontWeight: 600,
            color: '#fff',
            letterSpacing: '0.5px'
          }}>
            POS Admin
          </Typography>
          <Typography variant="body2" sx={{ 
            color: 'rgba(255,255,255,0.7)',
            fontSize: '0.875rem'
          }}>
            Sales Dashboard
          </Typography>
        </Box>
      )}

      <List sx={{ px: isMobile ? 0.5 : 2, py: 1 }}>
        {menuItems.map((item) => (
          <ListItem
            key={item.path}
            component={Link}
            to={item.path}
            sx={{
              mb: 0.5,
              borderRadius: 2,
              color: '#333',
              backgroundColor: location.pathname === item.path 
                ? 'rgba(25, 118, 210, 0.08)'
                : 'transparent',
              '&:hover': {
                backgroundColor: 'rgba(0,0,0,0.04)',
              },
              transition: 'all 0.2s ease',
              padding: isMobile ? '10px 8px' : '10px 16px',
              justifyContent: isMobile ? 'center' : 'flex-start',
            }}
          >
            <ListItemIcon sx={{ 
              minWidth: isMobile ? 0 : 40,
              color: location.pathname === item.path 
                ? '#1976d2'
                : 'rgba(0,0,0,0.54)',
              marginRight: isMobile ? 0 : 'auto'
            }}>
              {item.icon}
            </ListItemIcon>
            {!isMobile && (
              <ListItemText 
                primary={item.name}
                primaryTypographyProps={{
                  fontSize: '0.9375rem',
                  fontWeight: location.pathname === item.path ? 600 : 400,
                  color: location.pathname === item.path ? '#1976d2' : '#333'
                }}
              />
            )}
          </ListItem>
        ))}
      </List>
    </Drawer>
  );
}

export default Sidebar;
