import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { 
  Drawer, 
  List, 
  ListItem, 
  ListItemText, 
  Typography,
  Box
} from '@mui/material';

function Sidebar() {
  const location = useLocation();

  const menuItems = [
    { name: 'General', path: '/admin/sales' },
    { name: 'Gaudi', path: '/admin/sales/gaudi' },
    { name: 'Paralel', path: '/admin/sales/paralel' },
    { name: 'Mallorca', path: '/admin/sales/mallorca' },
    { name: 'Consell', path: '/admin/sales/consell' },
    { name: 'Hospital', path: '/admin/sales/hospital' }
  ];

  return (
    <Drawer
      variant="permanent"
      sx={{
        width: 240,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: 240,
          boxSizing: 'border-box',
          backgroundColor: '#1e293b',
          color: 'white'
        },
      }}
    >
      <Box sx={{ p: 3 }}>
        <Typography variant="h6" sx={{ mb: 3, fontWeight: 'bold' }}>
          POS Admin
        </Typography>
        <List>
          {menuItems.map((item) => (
            <ListItem
              key={item.path}
              component={Link}
              to={item.path}
              sx={{
                mb: 1,
                borderRadius: 1,
                backgroundColor: location.pathname === item.path ? '#2563eb' : 'transparent',
                '&:hover': {
                  backgroundColor: location.pathname === item.path ? '#2563eb' : '#374151',
                },
              }}
            >
              <ListItemText primary={item.name} />
            </ListItem>
          ))}
        </List>
      </Box>
    </Drawer>
  );
}

export default Sidebar;
