import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import API from '../../services/api';
import { generateInvoice } from '../../utils/invoice';
import Sidebar from './Sidebar';

const STORE_IDS = {
  gaudi: 5,
  paralel: 1,
  mallorca: 3,
  hospital: 7,
  consell: 14
};

function AdminSales() {
  const [sales, setSales] = useState([]);
  const [loading, setLoading] = useState(true);
  const [timeFilter, setTimeFilter] = useState('day');
  const [todayRevenue, setTodayRevenue] = useState(0);
  const [productStats, setProductStats] = useState([]);
  const [selectedVat, setSelectedVat] = useState(0);
  const [yesterdayRevenue, setYesterdayRevenue] = useState(0);
  const [periodRevenue, setPeriodRevenue] = useState(0);
  const navigate = useNavigate();
  const { store } = useParams();

  // For chart visualization - this would be populated from your API data
  const [chartData, setChartData] = useState([]);

  const fetchSales = useCallback(async () => {
    try {
      setLoading(true);
      const response = await API.get('/api/sales', {
        params: {
          'filters[store][id][$eq]': STORE_IDS[store],
          'populate': {
            'invoice': true,
            'product': true,
            '*': true
          },
          'sort': 'createdAt:desc'
        }
      });

      const sortedSales = response.data.data.sort((a, b) => 
        new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
      );

      setSales(sortedSales);
    } catch (error) {
      console.error('Error fetching sales:', error);
      if (error.response?.status === 401) {
        localStorage.removeItem('jwtToken');
        navigate('/login');
      }
    } finally {
      setLoading(false);
    }
  }, [store, navigate]);

  const fetchProductStats = useCallback(async () => {
    try {
      // Get store-specific sales
      const response = await API.get('/api/sales', {
        params: {
          'filters[store][id][$eq]': STORE_IDS[store],
          'populate': ['product', 'store']
        }
      });
      const storeSales = response.data.data;

      // Map products to their stats (full list from ProductStats.jsx)
      const stats = [
        { id: 1, name: "Phone", count: 0, totalRevenue: 0 },
        { id: 3, name: "Laptop", count: 0, totalRevenue: 0 },
        { id: 5, name: "Funda", count: 0, totalRevenue: 0 },
        { id: 7, name: "Charger", count: 0, totalRevenue: 0 },
        { id: 9, name: "Figura", count: 0, totalRevenue: 0 },
        { id: 11, name: "Voda SIM", count: 0, totalRevenue: 0 },
        { id: 13, name: "Protector", count: 0, totalRevenue: 0 },
        { id: 15, name: "Hydrogel Protector", count: 0, totalRevenue: 0 },
        { id: 17, name: "Cordón", count: 0, totalRevenue: 0 },
        { id: 19, name: "BT Headphones", count: 0, totalRevenue: 0 },
        { id: 21, name: "BT Speaker", count: 0, totalRevenue: 0 },
        { id: 23, name: "Souvenir", count: 0, totalRevenue: 0 },
        { id: 25, name: "Headphones", count: 0, totalRevenue: 0 },
        { id: 27, name: "Cable", count: 0, totalRevenue: 0 },
        { id: 29, name: "Cargador Set", count: 0, totalRevenue: 0 },
        { id: 31, name: "Power Bank", count: 0, totalRevenue: 0 },
        { id: 33, name: "Recarga", count: 0, totalRevenue: 0 },
        { id: 35, name: "Print", count: 0, totalRevenue: 0 },
        { id: 37, name: "Adapter", count: 0, totalRevenue: 0 },
        { id: 39, name: "Gafas", count: 0, totalRevenue: 0 },
        { id: 41, name: "Repair", count: 0, totalRevenue: 0 },
        { id: 43, name: "Data transfer", count: 0, totalRevenue: 0 },
        { id: 45, name: "Smartwatch", count: 0, totalRevenue: 0 },
        { id: 47, name: "Lyca SIM", count: 0, totalRevenue: 0 },
        { id: 49, name: "Raton", count: 0, totalRevenue: 0 },
        { id: 51, name: "Watch Strap", count: 0, totalRevenue: 0 },
        { id: 53, name: "USB Drive", count: 0, totalRevenue: 0 },
        { id: 55, name: "Drink", count: 0, totalRevenue: 0 },
        { id: 57, name: "Umbrella", count: 0, totalRevenue: 0 },
        { id: 59, name: "Car Phone Mount", count: 0, totalRevenue: 0 },
        { id: 61, name: "Shave Machine", count: 0, totalRevenue: 0 },
        { id: 63, name: "Funda headphone", count: 0, totalRevenue: 0 },
        { id: 65, name: "iPad", count: 0, totalRevenue: 0 },
        { id: 67, name: "iPad funda", count: 0, totalRevenue: 0 },
        { id: 69, name: "Macbook protector", count: 0, totalRevenue: 0 },
        { id: 71, name: "Anime shirt", count: 0, totalRevenue: 0 },
        { id: 73, name: "PC", count: 0, totalRevenue: 0 },
        { id: 75, name: "Keyboard", count: 0, totalRevenue: 0 },
        { id: 77, name: "Laptop (not MacBook)", count: 0, totalRevenue: 0 },
        { id: 79, name: "iMac", count: 0, totalRevenue: 0 },
        { id: 82, name: "Lyca SIM", count: 0, totalRevenue: 0 },
        { id: 85, name: "Selfie Stick", count: 0, totalRevenue: 0 },
        { id: 87, name: "OTG", count: 0, totalRevenue: 0 },
        { id: 89, name: "Pilas", count: 0, totalRevenue: 0 },
        { id: 91, name: "Soporte", count: 0, totalRevenue: 0 },
        { id: 93, name: "SD Card", count: 0, totalRevenue: 0 },
        { id: 95, name: "Dryer", count: 0, totalRevenue: 0 },
        { id: 97, name: "Plancha", count: 0, totalRevenue: 0 },
        { id: 99, name: "Mando", count: 0, totalRevenue: 0 },
        { id: 101, name: "Trimmer", count: 0, totalRevenue: 0 }
      ];

      // Process each sale
      storeSales.forEach(sale => {
        const productId = sale.product?.id;
        const price = parseFloat(sale.Price || 0);

        const productStat = stats.find(s => s.id === productId);
        if (productStat) {
          productStat.count += 1;
          productStat.totalRevenue += price;
        }
      });

      // Sort by revenue
      stats.sort((a, b) => b.totalRevenue - a.totalRevenue);
      setProductStats(stats);
    } catch (error) {
      console.error('Error:', error);
    }
  }, [store]);

  // New function to generate chart data
  const generateChartData = useCallback(() => {
    // This will process your sales data into a format suitable for the chart
    if (!sales || sales.length === 0) return [];

    // Group sales by date
    const dateMap = {};

    sales.forEach(sale => {
      if (!sale.Time) return;

      const date = new Date(sale.Time).toLocaleDateString('es-ES');
      if (!dateMap[date]) {
        dateMap[date] = 0;
      }
      dateMap[date] += parseFloat(sale.Price || 0);
    });

    // Convert to array for chart display, sorted by date
    const chartPoints = Object.keys(dateMap).map(date => ({
      date,
      amount: dateMap[date]
    })).sort((a, b) => new Date(a.date) - new Date(b.date));

    return chartPoints;
  }, [sales]);

  useEffect(() => {
    const token = localStorage.getItem('jwtToken');
    if (!token) {
      navigate('/login');
      return;
    }

    // Configure API token
    API.defaults.headers.common['Authorization'] = `Bearer ${token}`;

    // Only fetch data if we have a valid token
    fetchSales();
    if (store) {
      fetchProductStats();
    }
  }, [store, timeFilter, fetchSales, fetchProductStats, navigate]);

  useEffect(() => {
    // Add chart data generation to your existing useEffect
    if (sales && sales.length > 0) {
      setChartData(generateChartData());
    }
  }, [sales, generateChartData]);

  const handleDownloadInvoice = async (sale) => {
    // Find related sales either by orderGroupId or by time (within 1 second)
    let relatedSales;

    if (sale.orderGroupId) {
      // If orderGroupId exists, use it
      relatedSales = sales.filter(s => s.orderGroupId === sale.orderGroupId);
    } else {
      // Fallback to time-based grouping for older sales
      const saleTime = new Date(sale.Time).getTime();
      relatedSales = sales.filter(s => {
        const timeDiff = Math.abs(new Date(s.Time).getTime() - saleTime);
        return timeDiff < 1000;
      });
    }

    console.log('Related sales:', relatedSales);

    if (relatedSales.length === 0) {
      console.error('No related sales found for sale:', sale);
      return;
    }

    const items = relatedSales.map(s => ({
      product: { 
        Product: s.product?.Product || 'Producto no especificado'
      },
      price: s.Price,
      description: s.description || ''
    }));

    const total = relatedSales.reduce((sum, s) => sum + s.Price, 0);

    await generateInvoice(items, total, sale, selectedVat);
  };

  if (loading) return (
    <div style={{
      display: 'flex',
      minHeight: '100vh',
      backgroundColor: 'hsl(0 0% 98%)',
      padding: '32px'
    }}>
      <Sidebar />
      <main style={{
        flex: 1,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        fontFamily: 'system-ui',
        color: 'hsl(215.4 16.3% 46.9%)'
      }}>
        Loading sales data...
      </main>
    </div>
  );

  return (
    <div style={{ 
      display: 'flex',
      minHeight: '100vh',
      backgroundColor: 'hsl(0 0% 98%)',
      color: 'hsl(222.2 47.4% 11.2%)'
    }}>
      <Sidebar />

      <main style={{
        flex: 1,
        padding: '32px',
        backgroundColor: 'hsl(0 0% 98%)'
      }}>
        <div style={{
          maxWidth: '1400px',
          margin: '0 auto'
        }}>
          {/* Header Section with Integrated Filter */}
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '24px'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
              <h1 style={{
                fontSize: '24px',
                fontWeight: '600',
                color: 'hsl(222.2 47.4% 11.2%)',
                margin: 0,
                fontFamily: 'system-ui',
                letterSpacing: '-0.025em'
              }}>
                {store ? `${store.charAt(0).toUpperCase() + store.slice(1)} Store` : 'All Stores'}
              </h1>

              <select
                value={timeFilter}
                onChange={(e) => setTimeFilter(e.target.value)}
                style={{
                  padding: '8px 14px',
                  borderRadius: '6px',
                  border: '1px solid hsl(240 5.9% 90%)',
                  fontSize: '14px',
                  color: 'hsl(222.2 47.4% 11.2%)',
                  backgroundColor: 'white',
                  cursor: 'pointer',
                  fontFamily: 'system-ui',
                  outline: 'none'
                }}
              >
                <option value="all">All Time</option>
                <option value="day">Today</option>
                <option value="week">Last 7 Days</option>
                <option value="month">Last 30 Days</option>
                <option value="3months">Last 3 Months</option>
                <option value="12months">Last 12 Months</option>
              </select>
            </div>
          </div>

          {/* Revenue Chart Card */}
          <div style={{
            padding: '24px',
            backgroundColor: 'white',
            borderRadius: '8px',
            border: '1px solid hsl(240 5.9% 90%)',
            boxShadow: '0 1px 3px rgba(0, 0, 0, 0.05)',
            marginBottom: '24px'
          }}>
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '16px'
            }}>
              <h2 style={{
                fontSize: '16px',
                fontWeight: '600',
                color: 'hsl(222.2 47.4% 11.2%)',
                margin: 0,
                fontFamily: 'system-ui'
              }}>
                Revenue Trend
              </h2>

              <div style={{
                display: 'flex',
                gap: '16px'
              }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '6px' }}>
                  <div style={{ 
                    width: '12px', 
                    height: '12px', 
                    backgroundColor: 'hsl(222.2 47.4% 11.2%)',
                    borderRadius: '2px'
                  }}></div>
                  <span style={{ 
                    fontSize: '13px',
                    color: 'hsl(215.4 16.3% 46.9%)',
                    fontFamily: 'system-ui'
                  }}>
                    Daily Revenue
                  </span>
                </div>
              </div>
            </div>

            {/* Chart visualization */}
            <div style={{ height: '200px', position: 'relative' }}>
              {chartData.length > 0 ? (
                <div style={{
                  display: 'flex',
                  height: '100%',
                  alignItems: 'flex-end',
                  gap: '6px',
                  paddingBottom: '30px'
                }}>
                  {chartData.map((point, index) => {
                    // Calculate height as a percentage of the max value
                    const max = Math.max(...chartData.map(p => p.amount));
                    const height = (point.amount / max) * 100;

                    return (
                      <div key={index} style={{ flex: 1, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                        <div 
                          style={{
                            width: '100%',
                            height: `${height}%`,
                            backgroundColor: 'hsl(222.2 83.2% 90%)',
                            borderRadius: '3px 3px 0 0',
                            position: 'relative',
                            transition: 'height 0.3s ease',
                            minHeight: '5px'
                          }}
                          title={`€${point.amount.toFixed(2)}`}
                        ></div>
                        <div style={{ 
                          position: 'absolute',
                          bottom: '8px',
                          fontSize: '10px',
                          color: 'hsl(215.4 16.3% 46.9%)',
                          transform: 'rotate(-45deg)',
                          transformOrigin: 'top left',
                          whiteSpace: 'nowrap'
                        }}>
                          {point.date}
                        </div>
                      </div>
                    );
                  })}
                </div>
              ) : (
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  height: '100%',
                  color: 'hsl(215.4 16.3% 46.9%)',
                  fontSize: '14px',
                  fontFamily: 'system-ui'
                }}>
                  No data available for the selected period
                </div>
              )}
            </div>
          </div>

          {/* Stats Cards */}
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(3, 1fr)',
            gap: '20px',
            marginBottom: '24px'
          }}>
            {/* Today's Revenue */}
            <div style={{
              padding: '24px',
              backgroundColor: 'white',
              borderRadius: '8px',
              border: '1px solid hsl(240 5.9% 90%)',
              boxShadow: '0 1px 3px rgba(0, 0, 0, 0.05)',
              transition: 'transform 0.2s ease, box-shadow 0.2s ease'
            }}>
              <div style={{
                fontSize: '13px',
                fontWeight: '500',
                color: 'hsl(215.4 16.3% 46.9%)',
                marginBottom: '8px',
                fontFamily: 'system-ui'
              }}>
                Today's Revenue
              </div>
              <div style={{
                fontSize: '24px',
                fontWeight: '600',
                color: 'hsl(222.2 47.4% 11.2%)',
                fontFamily: 'system-ui'
              }}>
                €{todayRevenue.toFixed(2)}
              </div>
            </div>

            {/* Yesterday's Revenue */}
            <div style={{
              padding: '24px',
              backgroundColor: 'white',
              borderRadius: '8px',
              border: '1px solid hsl(240 5.9% 90%)',
              boxShadow: '0 1px 3px rgba(0, 0, 0, 0.05)'
            }}>
              <div style={{
                fontSize: '13px',
                fontWeight: '500',
                color: 'hsl(215.4 16.3% 46.9%)',
                marginBottom: '8px',
                fontFamily: 'system-ui'
              }}>
                Yesterday's Revenue
              </div>
              <div style={{
                fontSize: '24px',
                fontWeight: '600',
                color: 'hsl(222.2 47.4% 11.2%)',
                fontFamily: 'system-ui'
              }}>
                €{yesterdayRevenue.toFixed(2)}
              </div>
            </div>

            {/* Period Revenue */}
            <div style={{
              padding: '24px',
              backgroundColor: 'white',
              borderRadius: '8px',
              border: '1px solid hsl(240 5.9% 90%)',
              boxShadow: '0 1px 3px rgba(0, 0, 0, 0.05)'
            }}>
              <div style={{
                fontSize: '13px',
                fontWeight: '500',
                color: 'hsl(215.4 16.3% 46.9%)',
                marginBottom: '8px',
                fontFamily: 'system-ui'
              }}>
                {timeFilter === '12months' ? 'Yearly' : 
                 timeFilter === '3months' ? 'Quarterly' : 
                 'Monthly'} Revenue
              </div>
              <div style={{
                fontSize: '24px',
                fontWeight: '600',
                color: 'hsl(222.2 47.4% 11.2%)',
                fontFamily: 'system-ui'
              }}>
                €{periodRevenue.toFixed(2)}
              </div>
            </div>
          </div>

          {/* Main Content Grid */}
          <div style={{
            display: 'grid',
            gridTemplateColumns: '1fr auto',
            gap: '24px'
          }}>
            {/* Sales Table */}
            <div style={{
              backgroundColor: 'white',
              borderRadius: '8px',
              border: '1px solid hsl(240 5.9% 90%)',
              boxShadow: '0 1px 3px rgba(0, 0, 0, 0.05)',
              overflow: 'hidden'
            }}>
              <div style={{
                padding: '16px 24px',
                borderBottom: '1px solid hsl(240 5.9% 90%)'
              }}>
                <h2 style={{
                  fontSize: '16px',
                  fontWeight: '600',
                  color: 'hsl(222.2 47.4% 11.2%)',
                  fontFamily: 'system-ui'
                }}>
                  Recent Sales
                </h2>
              </div>

              <div style={{
                overflowX: 'auto'
              }}>
                <table style={{
                  width: '100%',
                  borderCollapse: 'collapse'
                }}>
                  <thead>
                    <tr style={{
                      backgroundColor: 'hsl(0 0% 98%)',
                      borderBottom: '1px solid hsl(240 5.9% 90%)'
                    }}>
                      <th style={{
                        padding: '12px 24px',
                        textAlign: 'left',
                        fontSize: '12px',
                        fontWeight: '500',
                        color: 'hsl(215.4 16.3% 46.9%)',
                        fontFamily: 'system-ui'
                      }}>Date</th>
                      <th style={{ ...headerStyle }}>Time</th>
                      <th style={{ ...headerStyle }}>Product</th>
                      <th style={{ ...headerStyle }}>Price</th>
                      <th style={{ ...headerStyle }}>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {sales.map((sale) => (
                      <tr 
                        key={sale.id}
                        style={{
                          borderBottom: '1px solid hsl(240 5.9% 90%)',
                          backgroundColor: 'white',
                          transition: 'background-color 0.15s ease'
                        }}
                        onMouseEnter={(e) => {
                          e.currentTarget.style.backgroundColor = 'hsl(210 40% 98%)';
                        }}
                        onMouseLeave={(e) => {
                          e.currentTarget.style.backgroundColor = 'white';
                        }}
                      >
                        <td style={{ ...cellStyle }}>
                          {sale.Time ? new Date(sale.Time).toLocaleDateString('es-ES') : 'N/A'}
                        </td>
                        <td style={{ ...cellStyle }}>
                          {sale.Time ? new Date(sale.Time).toLocaleTimeString('es-ES') : 'N/A'}
                        </td>
                        <td style={{ ...cellStyle }}>
                          {sale.product?.Product || 'N/A'}
                        </td>
                        <td style={{ ...cellStyle }}>
                          €{sale.Price?.toFixed(2) || '0.00'}
                        </td>
                        <td style={{ ...cellStyle }}>
                          <div style={{
                            display: 'flex',
                            gap: '8px',
                            alignItems: 'center'
                          }}>
                            <select
                              value={selectedVat}
                              onChange={(e) => setSelectedVat(Number(e.target.value))}
                              style={{
                                padding: '6px 10px',
                                borderRadius: '6px',
                                border: '1px solid hsl(240 5.9% 90%)',
                                fontSize: '13px',
                                color: 'hsl(222.2 47.4% 11.2%)',
                                backgroundColor: 'white',
                                cursor: 'pointer',
                                fontFamily: 'system-ui'
                              }}
                            >
                              <option value={0}>IVA: 0%</option>
                              <option value={10}>IVA: 10%</option>
                              <option value={21}>IVA: 21%</option>
                            </select>
                            <button
                              onClick={() => handleDownloadInvoice(sale)}
                              style={{
                                padding: '6px 12px',
                                backgroundColor: 'hsl(222.2 47.4% 11.2%)',
                                color: 'white',
                                border: 'none',
                                borderRadius: '6px',
                                cursor: 'pointer',
                                fontSize: '13px',
                                fontWeight: '500',
                                transition: 'all 0.15s ease',
                                fontFamily: 'system-ui'
                              }}
                            >
                              Download
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>

            {/* Product Stats */}
            <div style={{
              width: '400px',
              backgroundColor: 'white',
              borderRadius: '8px',
              border: '1px solid hsl(240 5.9% 90%)',
              boxShadow: '0 1px 3px rgba(0, 0, 0, 0.05)',
              overflow: 'hidden'
            }}>
              <div style={{
                padding: '16px 24px',
                borderBottom: '1px solid hsl(240 5.9% 90%)'
              }}>
                <h2 style={{
                  fontSize: '16px',
                  fontWeight: '600',
                  color: 'hsl(222.2 47.4% 11.2%)',
                  fontFamily: 'system-ui'
                }}>
                  Product Stats
                </h2>
              </div>

              <div style={{
                maxHeight: 'calc(100vh - 300px)',
                overflowY: 'auto'
              }}>
                <table style={{
                  width: '100%',
                  borderCollapse: 'collapse'
                }}>
                  <thead>
                    <tr style={{
                      backgroundColor: 'hsl(0 0% 98%)',
                      borderBottom: '1px solid hsl(240 5.9% 90%)'
                    }}>
                      <th style={{
                        padding: '12px 24px',
                        textAlign: 'left',
                        fontSize: '12px',
                        fontWeight: '500',
                        color: 'hsl(215.4 16.3% 46.9%)',
                        fontFamily: 'system-ui'
                      }}>Product</th>
                      <th style={{ ...headerStyle, textAlign: 'right' }}>Sales</th>
                      <th style={{ ...headerStyle, textAlign: 'right' }}>Revenue</th>
                    </tr>
                  </thead>
                  <tbody>
                    {productStats.map((stat) => (
                      <tr 
                        key={stat.id}
                        style={{
                          borderBottom: '1px solid hsl(240 5.9% 90%)',
                          backgroundColor: 'white',
                          transition: 'background-color 0.15s ease'
                        }}
                        onMouseEnter={(e) => {
                          e.currentTarget.style.backgroundColor = 'hsl(210 40% 98%)';
                        }}
                        onMouseLeave={(e) => {
                          e.currentTarget.style.backgroundColor = 'white';
                        }}
                      >
                        <td style={{ ...cellStyle }}>{stat.name}</td>
                        <td style={{ ...cellStyle, textAlign: 'right' }}>{stat.count}</td>
                        <td style={{ ...cellStyle, textAlign: 'right' }}>€{stat.totalRevenue.toFixed(2)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

const headerStyle = {
  padding: '12px 24px',
  textAlign: 'left',
  fontSize: '12px',
  fontWeight: '500',
  color: 'hsl(215.4 16.3% 46.9%)',
  fontFamily: 'system-ui'
};

const cellStyle = {
  padding: '12px 24px',
  fontSize: '13px',
  color: 'hsl(222.2 47.4% 11.2%)',
  fontFamily: 'system-ui'
};

export default AdminSales;
