import React, { useState } from 'react';
import API from '../services/api';

function ProductBlock({ product }) {
  const [price, setPrice] = useState('');
  const [showInput, setShowInput] = useState(false);

  const handleClick = () => {
    setShowInput(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await API.post('/sales', {
        data: {
          // Send the product ID and the manually entered price
          product: product.id,
          price: parseFloat(price),
        },
      });
      alert('Sale recorded!');
      setShowInput(false);
      setPrice('');
    } catch (error) {
      console.error('Error recording sale:', error);
      alert('Failed to record sale.');
    }
  };

  return (
    <div
      className="product-block"
      style={{ margin: '10px', padding: '10px', border: '1px solid #ccc', flex: '1 0 21%', textAlign: 'center' }}
    >
      <button onClick={handleClick} style={{ fontSize: '16px', padding: '10px', width: '100%' }}>
        {product.name}
      </button>
      {showInput && (
        <form onSubmit={handleSubmit} style={{ marginTop: '10px' }}>
          <input
            type="number"
            step="0.01"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            placeholder="Enter Price"
            required
            style={{ width: '80%', padding: '8px' }}
          />
          <button type="submit" style={{ padding: '8px', marginLeft: '5px' }}>
            Submit
          </button>
        </form>
      )}
    </div>
  );
}

export default ProductBlock;
