import React, { useState } from 'react';
import API from '../services/api';

function ProductBlock({ product, onAddToCart }) {
  const [price, setPrice] = useState('');
  const [showInput, setShowInput] = useState(false);

  console.log('ProductBlock received:', product);
  console.log('Full product data:', product);

  const productName = product?.Product || 'Unnamed Product';

  const handleClick = () => {
    setShowInput(true);
  };

  const handlePriceChange = (e) => {
    const value = e.target.value;
    // Convert to decimal format (e.g., "123" becomes "1.23")
    const numericValue = value.replace(/[^0-9]/g, '');
    const decimalValue = (parseInt(numericValue) / 100).toFixed(2);
    setPrice(decimalValue);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddToCart(product, price);
    setShowInput(false);
    setPrice('');
  };

  return (
    <div
      className="product-block"
      style={{ margin: '10px', padding: '10px', border: '1px solid #ccc', flex: '1 0 21%', textAlign: 'center' }}
    >
      <button onClick={handleClick} style={{ fontSize: '16px', padding: '10px', width: '100%' }}>
        {productName}
      </button>
      {showInput && (
        <form onSubmit={handleSubmit} style={{ marginTop: '10px' }}>
          <input
            type="text"
            value={price ? `$${price}` : ''}
            onChange={handlePriceChange}
            placeholder="Enter Price"
            required
            style={{ width: '80%', padding: '8px', fontSize: '18px', textAlign: 'center' }}
          />
          <button type="submit" style={{ padding: '8px', marginLeft: '5px' }}>
            Submit
          </button>
        </form>
      )}
    </div>
  );
}

export default ProductBlock;
