import jsPDF from 'jspdf';

export const generateInvoice = (items, total) => {
  const doc = new jsPDF();
  const pageWidth = doc.internal.pageSize.width;

  // Company Logo (you'll need to add your logo file)
  // doc.addImage('logo.png', 'PNG', 20, 10, 40, 40);

  // Company Info
  doc.setFontSize(20);
  doc.setFont(undefined, 'bold');
  doc.text('GAUDI STORE', 20, 20);

  doc.setFontSize(10);
  doc.setFont(undefined, 'normal');
  doc.text([
    'Carrer de Mallorca, 401',
    'Barcelona, Spain 08013',
    'Tel: +34 123 456 789',
    'Email: info@gaudistore.com'
  ], 20, 30);

  // Invoice Title
  doc.setFontSize(16);
  doc.setFont(undefined, 'bold');
  doc.text('INVOICE', pageWidth/2, 20, { align: 'center' });

  // Date and Invoice Number
  doc.setFontSize(10);
  doc.setFont(undefined, 'normal');
  doc.text(`Date: ${new Date().toLocaleDateString()}`, pageWidth - 60, 30);
  doc.text(`Invoice #: ${Math.floor(Math.random() * 10000)}`, pageWidth - 60, 35);

  // Line separator
  doc.line(20, 45, pageWidth - 20, 45);

  // Table Headers
  let yPos = 55;
  doc.setFont(undefined, 'bold');
  doc.text('Product', 20, yPos);
  doc.text('Price', pageWidth - 60, yPos);

  // Add items
  doc.setFont(undefined, 'normal');
  yPos += 10;
  items.forEach(item => {
    doc.text(item.product.Product, 20, yPos);
    doc.text(`€${item.price.toFixed(2)}`, pageWidth - 60, yPos);
    yPos += 8;
  });

  // Total
  yPos += 10;
  doc.line(20, yPos, pageWidth - 20, yPos);
  yPos += 10;
  doc.setFont(undefined, 'bold');
  doc.text('Total:', 20, yPos);
  doc.text(`€${total.toFixed(2)}`, pageWidth - 60, yPos);

  // Footer
  const footer = [
    'Follow us:',
    '@gaudistore_bcn',
    'www.gaudistore.com',
    '© 2025 Gaudi Store. All rights reserved.'
  ];

  doc.setFontSize(8);
  doc.setFont(undefined, 'normal');
  yPos = doc.internal.pageSize.height - 30;
  footer.forEach(line => {
    doc.text(line, pageWidth/2, yPos, { align: 'center' });
    yPos += 5;
  });

  // Save the PDF
  doc.save(`gaudi-invoice-${new Date().toISOString().split('T')[0]}.pdf`);
};
