import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import API from '../../services/api';
import { generateInvoice } from '../../utils/invoice';
import Sidebar from './Sidebar';

const STORE_IDS = {
  gaudi: 7,
  paralel: 8,
  mallorca: 9,
  consell: 10,
  hospital: 11
};

function AdminSales() {
  const [sales, setSales] = useState([]);
  const [loading, setLoading] = useState(true);
  const [timeFilter, setTimeFilter] = useState('all');
  const navigate = useNavigate();
  const { store } = useParams();

  const fetchSales = async () => {
    try {
      let url = '/api/sales?populate=*';

      // Add store filter if we're not on the general page
      if (store && STORE_IDS[store]) {
        url += `&filters[store][id][$eq]=${STORE_IDS[store]}`;
      }

      const response = await API.get(url);
      let filteredSales = response.data.data;

      // Apply time filter
      const now = new Date();
      filteredSales = filteredSales.filter(sale => {
        const saleDate = new Date(sale.Time);
        switch (timeFilter) {
          case 'day':
            return saleDate.toDateString() === now.toDateString();
          case 'week':
            const weekAgo = new Date(now.setDate(now.getDate() - 7));
            return saleDate >= weekAgo;
          case 'month':
            const monthAgo = new Date(now.setMonth(now.getMonth() - 1));
            return saleDate >= monthAgo;
          case '3months':
            const threeMonthsAgo = new Date(now.setMonth(now.getMonth() - 3));
            return saleDate >= threeMonthsAgo;
          case '12months':
            const yearAgo = new Date(now.setFullYear(now.getFullYear() - 1));
            return saleDate >= yearAgo;
          default:
            return true;
        }
      });

      // Sort by date (newest first)
      const sortedSales = filteredSales.sort((a, b) => 
        new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
      );

      setSales(sortedSales);
    } catch (error) {
      console.error('Error fetching sales:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchSales();
  }, [store, timeFilter]);

  const handleDownloadInvoice = (sale) => {
    const items = [{
      product: { Product: sale.product.Product },
      price: sale.Price
    }];
    generateInvoice(items, sale.Price);
  };

  if (loading) return <div className="ml-64 p-8">Loading sales data...</div>;

  return (
    <div className="flex">
      <Sidebar />
      <div className="ml-64 p-8 w-full">
        <div className="flex justify-between items-center mb-6">
          <div>
            <h1 className="text-2xl font-bold">
              {store ? `${store.charAt(0).toUpperCase() + store.slice(1)} Sales` : 'All Stores Sales'}
            </h1>
            <div className="mt-4">
              <select
                value={timeFilter}
                onChange={(e) => setTimeFilter(e.target.value)}
                className="p-2 border rounded"
              >
                <option value="all">All Time</option>
                <option value="day">Today</option>
                <option value="week">Last 7 Days</option>
                <option value="month">Last 30 Days</option>
                <option value="3months">Last 3 Months</option>
                <option value="12months">Last 12 Months</option>
              </select>
            </div>
          </div>
          <button
            onClick={() => navigate('/admin')}
            className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
          >
            Logout
          </button>
        </div>

        <table className="w-full border-collapse mt-4">
          <thead>
            <tr>
              <th className="px-4 py-2 text-left font-medium text-gray-900">Store</th>
              <th className="px-4 py-2 text-left font-medium text-gray-900">Date</th>
              <th className="px-4 py-2 text-left font-medium text-gray-900">Time</th>
              <th className="px-4 py-2 text-left font-medium text-gray-900">Product</th>
              <th className="px-4 py-2 text-left font-medium text-gray-900">Price</th>
              <th className="px-4 py-2 text-left font-medium text-gray-900">Actions</th>
            </tr>
          </thead>
          <tbody className="bg-white">
            {sales && sales.length > 0 ? (
              sales.map((sale) => (
                <tr key={sale.id} className="border-b border-gray-200">
                  <td className="px-4 py-4 text-sm text-gray-500">{sale.store?.Name || 'Unknown Store'}</td>
                  <td className="px-4 py-4 text-sm text-gray-500">{sale.Time ? new Date(sale.Time).toLocaleDateString('es-ES') : 'N/A'}</td>
                  <td className="px-4 py-4 text-sm text-gray-500">{sale.Time ? new Date(sale.Time).toLocaleTimeString('es-ES') : 'N/A'}</td>
                  <td className="px-4 py-4 text-sm text-gray-500">{sale.product?.Product || 'N/A'}</td>
                  <td className="px-4 py-4 text-sm text-gray-500">â‚¬{sale.Price?.toFixed(2) || '0.00'}</td>
                  <td className="px-4 py-4 text-sm text-gray-500">
                    <button
                      onClick={() => handleDownloadInvoice(sale)}
                      className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                    >
                      Download Invoice
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" className="px-4 py-4 text-center text-sm text-gray-500">
                  No sales records found
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminSales;
