import jsPDF from 'jspdf';

export const generateInvoice = (items, total) => {
  const doc = new jsPDF();
  const pageWidth = doc.internal.pageSize.width;

  // Add header
  doc.setFontSize(20);
  doc.text('Invoice', pageWidth/2, 20, { align: 'center' });

  // Add date
  doc.setFontSize(12);
  doc.text(`Date: ${new Date().toLocaleDateString()}`, 20, 40);

  // Add items
  doc.setFontSize(12);
  let yPos = 60;

  // Table header
  doc.text('Product', 20, yPos);
  doc.text('Price', pageWidth - 50, yPos);
  yPos += 10;

  // Add line under headers
  doc.line(20, yPos, pageWidth - 20, yPos);
  yPos += 10;

  // Add items
  items.forEach(item => {
    doc.text(item.product.Product, 20, yPos);
    doc.text(`€${item.price}`, pageWidth - 50, yPos);
    yPos += 10;
  });

  // Add total
  yPos += 10;
  doc.line(20, yPos, pageWidth - 20, yPos);
  yPos += 10;
  doc.setFont(undefined, 'bold');
  doc.text('Total:', 20, yPos);
  doc.text(`€${total.toFixed(2)}`, pageWidth - 50, yPos);

  // Save the PDF
  doc.save(`invoice-${new Date().toISOString().split('T')[0]}.pdf`);
};
