import React from 'react';

function Cart({ items, removeFromCart, checkout }) {
  const total = items.reduce((sum, item) => sum + parseFloat(item.price), 0);

  return (
    <div style={{ 
      height: '100%',
      display: 'flex',
      flexDirection: 'column',
      padding: '24px',
      backgroundColor: 'hsl(0 0% 100%)'
    }}>
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: '24px'
      }}>
        <h2 style={{
          fontSize: '16px',
          fontWeight: '600',
          color: 'hsl(222.2 47.4% 11.2%)',
          margin: 0,
          fontFamily: 'system-ui'
        }}>Current Sale</h2>
        <span style={{
          fontSize: '13px',
          color: 'hsl(215.4 16.3% 46.9%)',
          fontFamily: 'system-ui'
        }}>{items.length} items</span>
      </div>

      <div style={{ 
        flex: 1,
        overflowY: 'auto',
        marginBottom: '24px'
      }}>
        {items.map((item, index) => (
          <div key={index} style={{ 
            display: 'flex', 
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '8px',
            padding: '12px',
            backgroundColor: 'hsl(0 0% 100%)',
            borderRadius: '6px',
            border: '1px solid hsl(240 5.9% 90%)'
          }}>
            <div style={{ flex: 1 }}>
              <span style={{ 
                fontSize: '14px',
                fontWeight: '500',
                color: 'hsl(222.2 47.4% 11.2%)',
                display: 'block',
                marginBottom: '2px',
                fontFamily: 'system-ui'
              }}>
                {item.product.Product}
              </span>
              {item.description && (
                <span style={{ 
                  fontSize: '13px', 
                  color: 'hsl(215.4 16.3% 46.9%)',
                  fontFamily: 'system-ui'
                }}>
                  {item.description}
                </span>
              )}
            </div>
            <div style={{ 
              display: 'flex', 
              alignItems: 'center',
              gap: '12px'
            }}>
              <span style={{ 
                fontSize: '14px',
                fontWeight: '500',
                color: 'hsl(222.2 47.4% 11.2%)',
                fontFamily: 'system-ui'
              }}>
                €{parseFloat(item.price).toFixed(2)}
              </span>
              <button 
                onClick={() => removeFromCart(index)}
                style={{ 
                  width: '24px',
                  height: '24px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  border: '1px solid hsl(240 5.9% 90%)',
                  borderRadius: '4px',
                  backgroundColor: 'transparent',
                  color: 'hsl(215.4 16.3% 46.9%)',
                  cursor: 'pointer',
                  transition: 'all 0.2s ease',
                  fontSize: '16px'
                }}
              >
                ×
              </button>
            </div>
          </div>
        ))}
      </div>

      <div style={{
        borderTop: '1px solid hsl(240 5.9% 90%)',
        paddingTop: '20px'
      }}>
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between',
          marginBottom: '20px'
        }}>
          <span style={{
            fontSize: '14px',
            fontWeight: '500',
            color: 'hsl(222.2 47.4% 11.2%)',
            fontFamily: 'system-ui'
          }}>Total</span>
          <span style={{
            fontSize: '14px',
            fontWeight: '600',
            color: 'hsl(222.2 47.4% 11.2%)',
            fontFamily: 'system-ui'
          }}>€{total.toFixed(2)}</span>
        </div>
        <button 
          onClick={checkout}
          disabled={items.length === 0}
          style={{ 
            width: '100%',
            padding: '10px 16px',
            fontSize: '14px',
            fontWeight: '500',
            backgroundColor: items.length ? 'hsl(222.2 47.4% 11.2%)' : 'hsl(240 5.9% 90%)',
            color: items.length ? 'white' : 'hsl(215.4 16.3% 46.9%)',
            border: 'none',
            borderRadius: '6px',
            cursor: items.length ? 'pointer' : 'not-allowed',
            transition: 'all 0.2s ease',
            fontFamily: 'system-ui'
          }}
        >
          Complete Sale
        </button>
      </div>
    </div>
  );
}

export default Cart;
