import React, { useEffect, useState } from 'react';
import API from '../services/api';
import ProductBlock from './ProductBlock';
import Cart from './Cart';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const token = localStorage.getItem('jwtToken');
        console.log('Using token:', token);

        const response = await API.get('/api/products');
        console.log('Full API Response:', JSON.stringify(response.data, null, 2));

        if (!response.data.data || response.data.data.length === 0) {
          console.error('No products found in response');
          return;
        }

        setProducts(response.data.data);
      } catch (error) {
        console.error('Error details:', error.response?.data);
        console.error('Full error:', error);
      }
    };

    fetchProducts();
  }, []);

  const addToCart = (product, price) => {
    setCartItems([...cartItems, { product, price }]);
  };

  const removeFromCart = (index) => {
    setCartItems(cartItems.filter((_, i) => i !== index));
  };

  const handleCheckout = async () => {
    try {
      // Create a sale record for each item
      const salePromises = cartItems.map(item => {
        const saleData = {
          data: {
            product: { connect: { id: item.product.id } },
            store: parseInt(localStorage.getItem('storeId')),
            Price: parseFloat(item.price),
            Time: new Date().toISOString()
          }
        };
        return API.post('/api/sales', saleData);
      });

      await Promise.all(salePromises);
      alert('Sales recorded successfully!');
      setCartItems([]); // Clear cart
    } catch (error) {
      console.error('Checkout error:', error);
      alert('Error processing checkout');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>POS System</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {products.map((product) => {
          console.log('Product data:', product);
          return (
            <ProductBlock 
              key={product.id} 
              product={product}
              onAddToCart={addToCart}
            />
          );
        })}
      </div>
      <Cart 
        items={cartItems} 
        removeFromCart={removeFromCart}
        checkout={handleCheckout}
      />
    </div>
  );
}

export default ProductList;
