import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:1337'
});

// Add a request interceptor
API.interceptors.request.use(
  config => {
    // For sales endpoint, use API token
    if (config.url.includes('/api/sales')) {
      config.headers.Authorization = `Bearer 6e3436f4f0d9316b36c794f1cff51a9bcea92e0dc60277837b094b9c0ca659b9f29474d417d886f70947242b9aff5d46ee27fe18fd778a33b02cec6b6aa0b8a11296ecff36fe02e99279ec3b87b36943bfaa30d501e1c2becbe81cda94c1be48f91c434a5bb117d1e1ab31be8fb0669f417464b554c38ec002d8ae753d6a215d`;
    } else {
      // For other endpoints, use JWT
      const token = localStorage.getItem('jwtToken');
      if (token) {
        config.headers.Authorization = `Bearer ${token}`;
      }
    }
    return config;
  },
  error => {
    return Promise.reject(error);
  }
);

export default API;
