import React, { useEffect, useState } from 'react';
import API from '../services/api';
import ProductBlock from './ProductBlock';
import Cart from './Cart';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [salesHistory, setSalesHistory] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const token = localStorage.getItem('jwtToken');
        console.log('Using token:', token);

        const response = await API.get('/api/products');
        console.log('Full API Response:', JSON.stringify(response.data, null, 2));

        if (!response.data.data || response.data.data.length === 0) {
          console.error('No products found in response');
          return;
        }

        setProducts(response.data.data);
      } catch (error) {
        console.error('Error details:', error.response?.data);
        console.error('Full error:', error);
      }
    };

    fetchProducts();
  }, []);

  const addToCart = (product, price) => {
    console.log('Adding to cart:', { product, price });
    setCartItems([...cartItems, { product, price }]);
  };

  const removeFromCart = (index) => {
    setCartItems(cartItems.filter((_, i) => i !== index));
  };

  const handleCheckout = async () => {
    try {
      console.log('Cart items at checkout:', cartItems);
      console.log('Cart items mapped:', cartItems.map(item => ({
        productId: item.product.id,
        productName: item.product.Product,
        price: item.price
      })));

      const salePromises = cartItems.map(item => {
        const saleData = {
          data: {
            product: {
              connect: [{ id: item.product.id }]
            },
            store: {
              connect: [{ id: parseInt(localStorage.getItem('storeId')) }]
            },
            Price: parseFloat(item.price),
            Time: new Date().toISOString()
          }
        };

        console.log('Creating sale for product:', {
          productId: item.product.id,
          productName: item.product.Product,
          price: item.price
        });

        console.log('Sale data being sent:', JSON.stringify(saleData, null, 2));

        return API.post('/api/sales?populate=*', saleData);
      });

      const responses = await Promise.all(salePromises);
      const newSales = responses.map(response => ({
        id: response.data.data.id,
        product: response.data.data.product.Product,
        price: response.data.data.Price,
        time: response.data.data.Time
      }));

      setSalesHistory(prev => [...newSales, ...prev].slice(0, 10));

      alert('Sales recorded successfully!');
      setCartItems([]);

    } catch (error) {
      console.error('Error details:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status
      });
      alert('Error processing checkout');
    }
  };

  return (
    <div style={{ 
      display: 'flex', 
      height: '100vh',
      width: '100vw',
      overflow: 'hidden'
    }}>
      {/* Updated Debug Panel */}
      {process.env.NODE_ENV === 'development' && (
        <div style={{
          position: 'fixed',
          top: 0,
          right: 0,
          background: 'rgba(0,0,0,0.8)',
          color: 'white',
          padding: '10px',
          fontSize: '12px',
          zIndex: 9999,
          maxHeight: '300px',
          overflowY: 'auto'
        }}>
          <h4>Debug Info</h4>
          <div>
            <strong>Products:</strong>
            <ul style={{ margin: '5px 0', paddingLeft: '20px' }}>
              {products.map(p => (
                <li key={p.id}>
                  {p.Product}: API ID={p.id}
                </li>
              ))}
            </ul>
          </div>
          <div>
            <strong>Cart Items:</strong>
            <ul style={{ margin: '5px 0', paddingLeft: '20px' }}>
              {cartItems.map((item, idx) => (
                <li key={idx}>
                  {item.product.Product}: ID={item.product.id}, Price=${item.price}
                </li>
              ))}
            </ul>
          </div>
          <div>
            <strong>Recent Sales:</strong>
            <ul style={{ margin: '5px 0', paddingLeft: '20px' }}>
              {salesHistory.map((sale, idx) => (
                <li key={idx}>
                  API Sale ID={sale.id}: {sale.product} - ${sale.price}
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}

      {/* Products Section - Left 50% */}
      <div style={{ 
        width: '50%',
        padding: '30px',  // More padding
        overflowY: 'auto',
        borderRight: '2px solid #eee',  // Thicker border
        backgroundColor: '#f8f9fa'  // Light background
      }}>
        <h2 style={{
          fontSize: '24px',
          marginBottom: '25px',
          color: '#333'
        }}>Products</h2>
        <div style={{ 
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',  // Wider blocks
          gap: '20px',  // More space between blocks
          padding: '10px'  // Padding for the grid
        }}>
          {products.map((product) => (
            <ProductBlock 
              key={product.id} 
              product={product}
              onAddToCart={addToCart}
            />
          ))}
        </div>
      </div>

      {/* Cart Section - Right 50% */}
      <div style={{ 
        width: '50%',
        height: '100%'
      }}>
        <Cart 
          items={cartItems} 
          removeFromCart={removeFromCart}
          checkout={handleCheckout}
        />
      </div>
    </div>
  );
}

export default ProductList;
