import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../services/api';
import { generateInvoice } from '../utils/invoice';

function SalesDashboard() {
  const [sales, setSales] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedVat, setSelectedVat] = useState(0);
  const [searchTerm, setSearchTerm] = useState('');
  const [todayRevenue, setTodayRevenue] = useState(0);
  const [yesterdayRevenue, setYesterdayRevenue] = useState(0);
  const [todayCashStats, setTodayCashStats] = useState({ count: 0, amount: 0 });
  const [todayCardStats, setTodayCardStats] = useState({ count: 0, amount: 0 });
  const navigate = useNavigate();
  const storeId = localStorage.getItem('storeId');

  useEffect(() => {
    const token = localStorage.getItem('jwtToken');
    if (!token) {
      navigate('/login');
      return;
    }
    const fetchSales = async () => {
      try {
        // Get today's sales
        const todayStart = new Date();
        todayStart.setHours(0, 0, 0, 0);

        const todayResponse = await API.get('/api/sales', {
          params: {
            'filters[store][id][$eq]': storeId,
            'filters[Time][$gte]': todayStart.toISOString(),
            'populate': ['*', 'product'],
          }
        });

        const todaySales = todayResponse.data?.data || [];

        // Calculate payment method stats
        const cashSales = todaySales.filter(sale => 
          sale.paymentmethod?.toLowerCase() === 'cash'
        );
        const cardSales = todaySales.filter(sale => 
          sale.paymentmethod?.toLowerCase() === 'card'
        );

        const cashTotal = cashSales.reduce((sum, sale) => 
          sum + (parseFloat(sale.Price) || 0), 0
        );

        const cardTotal = cardSales.reduce((sum, sale) => 
          sum + (parseFloat(sale.Price) || 0), 0
        );

        setTodayCashStats({
          count: cashSales.length,
          amount: cashTotal
        });

        setTodayCardStats({
          count: cardSales.length,
          amount: cardTotal
        });

        setTodayRevenue(cashTotal + cardTotal);

        // Get yesterday's sales
        const yesterdayStart = new Date();
        yesterdayStart.setDate(yesterdayStart.getDate() - 1);
        yesterdayStart.setHours(0, 0, 0, 0);
        const yesterdayEnd = new Date(todayStart);

        const yesterdayResponse = await API.get('/api/sales', {
          params: {
            'filters[store][id][$eq]': storeId,
            'filters[Time][$gte]': yesterdayStart.toISOString(),
            'filters[Time][$lt]': yesterdayEnd.toISOString(),
            'populate': ['*', 'product'],
          }
        });

        const yesterdayTotal = yesterdayResponse.data?.data?.reduce((sum, sale) => 
          sum + (parseFloat(sale.Price) || 0), 0
        ) || 0;
        setYesterdayRevenue(yesterdayTotal);

        console.log('Fetching sales for store:', storeId);
        const response = await API.get('/api/sales', {
          params: {
            'filters[store][id][$eq]': storeId,
            'populate': {
              'invoice': true,
              'product': true,
              '*': true
            },
            'sort': 'createdAt:desc'
          }
        });

        const sortedSales = response.data.data.sort((a, b) => 
          new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
        );

        console.log('Sorted sales:', sortedSales);
        setSales(sortedSales);
      } catch (error) {
        console.error('Error fetching sales:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchSales();
  }, [storeId, navigate]);

  const handleDownloadInvoice = async (sale) => {
    // Find related sales either by orderGroupId or by time (within 1 second)
    let relatedSales;

    if (sale.orderGroupId) {
      // If orderGroupId exists, use it
      relatedSales = sales.filter(s => s.orderGroupId === sale.orderGroupId);
    } else {
      // Fallback to time-based grouping for older sales
      const saleTime = new Date(sale.Time).getTime();
      relatedSales = sales.filter(s => {
        const timeDiff = Math.abs(new Date(s.Time).getTime() - saleTime);
        return timeDiff < 1000;
      });
    }

    console.log('Related sales:', relatedSales);

    if (relatedSales.length === 0) {
      console.error('No related sales found for sale:', sale);
      return;
    }

    const items = relatedSales.map(s => ({
      product: { 
        Product: s.product?.Product || 'Producto no especificado'
      },
      price: s.Price,
      description: s.description || ''
    }));

    console.log('Items for invoice:', items);

    const total = relatedSales.reduce((sum, s) => sum + s.Price, 0);

    await generateInvoice(items, total, sale, selectedVat);
  };

  // Add filtered sales logic
  const filteredSales = sales.filter(sale => {
    const searchLower = searchTerm.toLowerCase();
    return (
      (sale.product?.Product || '').toLowerCase().includes(searchLower) ||
      (sale.description || '').toLowerCase().includes(searchLower) ||
      (sale.Time ? new Date(sale.Time).toLocaleDateString('es-ES').includes(searchTerm) : false) ||
      (sale.Price?.toString() || '').includes(searchTerm)
    );
  });

  if (loading) {
    return (
      <div style={{
        height: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: 'hsl(215.4 16.3% 46.9%)',
        fontSize: '14px',
        fontWeight: '500',
        fontFamily: 'system-ui'
      }}>
        Loading sales data...
      </div>
    );
  }

  // --- Calculate a dynamic max-height for the table container ---
  const tableContainerMaxHeight = 'calc(100vh - 200px)'; 

  return (
    <div style={{ 
      padding: '32px',
      maxWidth: '1800px',
      margin: '0 auto',
      backgroundColor: 'hsl(0 0% 100%)',
      height: '100vh',
      display: 'flex',
      flexDirection: 'column'
    }}>
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        marginBottom: '32px',
        flexShrink: 0
      }}>
        <h1 style={{
          fontSize: '22px',
          fontWeight: '600',
          color: 'hsl(222.2 47.4% 11.2%)',
          margin: 0,
          fontFamily: 'system-ui',
          letterSpacing: '-0.025em'
        }}>
          Sales History
        </h1>

        <button
          onClick={() => navigate('/pos')}
          onMouseEnter={(e) => {
            e.currentTarget.style.backgroundColor = 'hsl(210 40% 98%)';
            e.currentTarget.style.borderColor = 'hsl(215.4 16.3% 46.9%)';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.backgroundColor = 'white';
            e.currentTarget.style.borderColor = 'hsl(240 5.9% 90%)';
          }}
          style={{
            padding: '10px 16px',
            backgroundColor: 'white',
            color: 'hsl(222.2 47.4% 11.2%)',
            border: '1px solid hsl(240 5.9% 90%)',
            borderRadius: '6px',
            cursor: 'pointer',
            fontSize: '14px',
            fontWeight: '500',
            transition: 'all 0.15s ease',
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
            fontFamily: 'system-ui'
          }}
        >
          <svg width="15" height="15" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M19 12H5M12 19l-7-7 7-7"/>
          </svg>
          Back to POS
        </button>
      </div>

      {/* Main Content Grid */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'minmax(0, 1fr) 400px',
        gap: '32px',
        height: 'calc(100vh - 100px)',
        minWidth: '1024px'
      }}>
        {/* Left Side - Sales Table */}
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          gap: '24px'
        }}>
          {/* Search Bar */}
          <div style={{
            marginBottom: '24px',
            display: 'flex',
            alignItems: 'center',
            gap: '12px',
            padding: '16px',
            backgroundColor: 'white',
            borderRadius: '8px',
            border: '1px solid hsl(240 5.9% 90%)',
            boxShadow: '0 1px 3px rgba(0, 0, 0, 0.05)'
          }}>
            <svg 
              width="16" 
              height="16" 
              viewBox="0 0 24 24" 
              fill="none" 
              stroke="currentColor" 
              strokeWidth="2"
              style={{ color: 'hsl(215.4 16.3% 46.9%)' }}
            >
              <circle cx="11" cy="11" r="8"/>
              <path d="m21 21-4.3-4.3"/>
            </svg>
            <input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Search by product, description, date, or price..."
              style={{
                flex: 1,
                border: 'none',
                outline: 'none',
                fontSize: '14px',
                color: 'hsl(222.2 47.4% 11.2%)',
                fontFamily: 'system-ui',
                '::placeholder': {
                  color: 'hsl(215.4 16.3% 46.9%)'
                }
              }}
            />
          </div>

          <div style={{
            backgroundColor: 'white',
            borderRadius: '8px',
            border: '1px solid hsl(240 5.9% 90%)',
            overflowY: 'auto',
            boxShadow: '0 1px 3px rgba(0, 0, 0, 0.05)',
            flexGrow: 1,
            maxHeight: tableContainerMaxHeight,
            position: 'relative'
          }}>
            <table style={{ 
              width: '100%', 
              borderCollapse: 'collapse',
              tableLayout: 'fixed'
            }}>
              <colgroup>
                <col style={{ width: '10%' }} />
                <col style={{ width: '8%' }} />
                <col style={{ width: '15%' }} />
                <col style={{ width: '20%' }} />
                <col style={{ width: '10%' }} />
                <col style={{ width: '12%' }} />
                <col style={{ width: '10%' }} />
                <col style={{ width: '15%' }} />
              </colgroup>
              <thead style={{
                position: 'sticky',
                top: 0,
                zIndex: 1,
              }}>
                <tr style={{
                  backgroundColor: 'white', 
                  borderBottom: '1.5px solid hsl(240 5.9% 90%)'
                }}>
                  <th style={{ 
                    padding: '12px 20px',
                    textAlign: 'left',
                    fontSize: '13px',
                    fontWeight: '600',
                    color: 'hsl(222.2 47.4% 11.2%)',
                    fontFamily: 'system-ui'
                  }}>Date</th>
                  <th style={{ ...headerStyle }}>Time</th>
                  <th style={{ ...headerStyle }}>Product</th>
                  <th style={{ ...headerStyle }}>Description</th>
                  <th style={{ ...headerStyle }}>Payment</th>
                  <th style={{ ...headerStyle }}>Amount Paid</th>
                  <th style={{ ...headerStyle }}>Price</th>
                  <th style={{ ...headerStyle }}>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredSales && filteredSales.length > 0 ? (
                  filteredSales.map((sale) => (
                    <tr 
                      key={sale.id} 
                      style={{ 
                        borderBottom: '1px solid hsl(240 5.9% 90%)',
                        transition: 'background-color 0.15s ease'
                      }}
                      onMouseEnter={(e) => {
                        e.currentTarget.style.backgroundColor = 'hsl(210 40% 98%)';
                      }}
                      onMouseLeave={(e) => {
                        e.currentTarget.style.backgroundColor = 'white';
                      }}
                    >
                      <td style={{ 
                        padding: '12px 20px',
                        fontSize: '13px',
                        color: 'hsl(222.2 47.4% 11.2%)',
                        fontFamily: 'system-ui'
                      }}>
                        {sale.Time ? new Date(sale.Time).toLocaleDateString('es-ES') : 'N/A'}
                      </td>
                      <td style={{ ...cellStyle }}>
                        {sale.Time ? new Date(sale.Time).toLocaleTimeString('es-ES') : 'N/A'}
                      </td>
                      <td style={{ ...cellStyle }}>
                        {sale.product?.Product || 'N/A'}
                      </td>
                      <td style={{ ...cellStyle }}>
                        {sale.description || 'No description'}
                      </td>
                      <td style={{ ...cellStyle, textTransform: 'capitalize' }}>
                        {sale.paymentmethod || 'N/A'}
                      </td>
                      <td style={{ ...cellStyle, fontWeight: '500' }}>
                        {typeof sale.paymentamount === 'number' ? `â‚¬${sale.paymentamount.toFixed(2)}` : 'N/A'}
                      </td>
                      <td style={{ ...cellStyle, fontWeight: '500' }}>
                        â‚¬{sale.Price?.toFixed(2) || '0.00'}
                      </td>
                      <td style={{ padding: '8px 20px' }}>
                        <div style={{ 
                          display: 'flex', 
                          gap: '8px', 
                          alignItems: 'center'
                        }}>
                          <select
                            value={selectedVat}
                            onChange={(e) => setSelectedVat(Number(e.target.value))}
                            style={{
                              padding: '6px 10px',
                              borderRadius: '6px',
                              border: '1px solid hsl(240 5.9% 90%)',
                              fontSize: '13px',
                              color: 'hsl(222.2 47.4% 11.2%)',
                              backgroundColor: 'white',
                              cursor: 'pointer',
                              fontFamily: 'system-ui'
                            }}
                          >
                            <option value={0}>IVA: 0%</option>
                            <option value={10}>IVA: 10%</option>
                            <option value={21}>IVA: 21%</option>
                          </select>
                          <button
                            onClick={() => handleDownloadInvoice(sale)}
                            onMouseEnter={(e) => {
                              e.currentTarget.style.backgroundColor = 'hsl(221.2 83.2% 53.3%)';
                            }}
                            onMouseLeave={(e) => {
                              e.currentTarget.style.backgroundColor = 'hsl(222.2 47.4% 11.2%)';
                            }}
                            style={{
                              padding: '6px 12px',
                              backgroundColor: 'hsl(222.2 47.4% 11.2%)',
                              color: 'white',
                              border: 'none',
                              borderRadius: '6px',
                              cursor: 'pointer',
                              fontSize: '13px',
                              fontWeight: '500',
                              transition: 'all 0.15s ease',
                              fontFamily: 'system-ui'
                            }}
                          >
                            Download Invoice
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td 
                      colSpan="8"
                      style={{ 
                        textAlign: 'center', 
                        padding: '24px',
                        color: 'hsl(215.4 16.3% 46.9%)',
                        fontSize: '13px',
                        fontFamily: 'system-ui'
                      }}
                    >
                      No sales records found
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>

        {/* Right Side - Revenue Widgets */}
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          gap: '24px'
        }}>
          {/* Today's Revenue */}
          <div style={{
            backgroundColor: 'white',
            borderRadius: '12px',
            border: '1px solid hsl(240 5.9% 90%)',
            padding: '32px',
            boxShadow: '0 1px 3px rgba(0, 0, 0, 0.05)'
          }}>
            <div style={{
              fontSize: '16px',
              fontWeight: '500',
              color: 'hsl(215.4 16.3% 46.9%)',
              marginBottom: '16px',
              fontFamily: 'system-ui'
            }}>
              Today's Revenue
            </div>
            <div style={{
              fontSize: '48px',
              fontWeight: '600',
              color: todayRevenue >= 500 ? 'hsl(142.1 76.2% 36.3%)' : 'hsl(24.6 95% 53.1%)',
              fontFamily: 'system-ui',
