import React, { useState } from 'react';
import API from '../services/api';

function ProductBlock({ product, storeId }) {
  const [price, setPrice] = useState('');
  const [showInput, setShowInput] = useState(false);

  console.log('ProductBlock received:', product);
  console.log('Full product data:', product);

  const productName = product?.attributes?.Product;

  const handleClick = () => {
    setShowInput(true);
  };

  const handlePriceChange = (e) => {
    const value = e.target.value;
    // Convert to decimal format (e.g., "123" becomes "1.23")
    const numericValue = value.replace(/[^0-9]/g, '');
    const decimalValue = (parseInt(numericValue) / 100).toFixed(2);
    setPrice(decimalValue);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const saleData = {
        data: {
          product: { connect: [{ id: product.id }] },
          store: { connect: [{ id: parseInt(storeId) }] },
          Price: parseFloat(price),
          Time: new Date()
        }
      };

      console.log('Sending sale data:', saleData);

      await API.post('/api/sales', saleData);
      alert('Sale recorded!');
      setShowInput(false);
      setPrice('');
    } catch (error) {
      console.error('Full error:', error);
      console.error('Error details:', error.response?.data);
      alert('Failed to record sale.');
    }
  };

  return (
    <div
      className="product-block"
      style={{ 
        margin: '10px', 
        padding: '10px', 
        border: '1px solid #ccc', 
        borderRadius: '5px',
        flex: '1 0 200px',  // Fixed width blocks
        maxWidth: '300px',  // Maximum width
        textAlign: 'center',
        backgroundColor: '#f8f9fa'
      }}
    >
      <button 
        onClick={handleClick} 
        style={{ 
          fontSize: '16px', 
          padding: '15px', 
          width: '100%',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer'
        }}
      >
        {productName || 'Unnamed Product'}
      </button>
      {showInput && (
        <form onSubmit={handleSubmit} style={{ marginTop: '10px' }}>
          <input
            type="text"
            value={price ? `$${price}` : ''}
            onChange={handlePriceChange}
            placeholder="Enter Price"
            required
            style={{ width: '80%', padding: '8px', fontSize: '18px', textAlign: 'center' }}
          />
          <button type="submit" style={{ padding: '8px', marginLeft: '5px' }}>
            Submit
          </button>
        </form>
      )}
    </div>
  );
}

export default ProductBlock;
