import React, { useState } from 'react';
import API from '../services/api';

function ProductBlock({ product, onAddToCart }) {
  const [price, setPrice] = useState('');
  const [showInput, setShowInput] = useState(false);

  console.log('ProductBlock received:', product);
  console.log('Full product data:', product);

  const productName = product?.Product || 'Unnamed Product';

  const handleClick = () => {
    setShowInput(true);
  };

  const handlePriceChange = (e) => {
    const value = e.target.value;
    // Convert to decimal format (e.g., "123" becomes "1.23")
    const numericValue = value.replace(/[^0-9]/g, '');
    const decimalValue = (parseInt(numericValue) / 100).toFixed(2);
    setPrice(decimalValue);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddToCart(product, price);
    setShowInput(false);
    setPrice('');
  };

  return (
    <div className="product-block" style={{ 
      height: '180px',  // Taller blocks
      display: 'flex',
      flexDirection: 'column',
      backgroundColor: 'white',
      borderRadius: '12px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
      overflow: 'hidden',
      transition: 'transform 0.2s, box-shadow 0.2s',
      cursor: 'pointer',
      ':hover': {
        transform: 'translateY(-2px)',
        boxShadow: '0 4px 12px rgba(0,0,0,0.15)'
      }
    }}>
      {/* Product Button */}
      <button 
        onClick={handleClick} 
        style={{ 
          flex: 1,
          width: '100%',
          padding: '20px',
          fontSize: '18px',
          fontWeight: '500',
          border: 'none',
          backgroundColor: 'white',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          cursor: 'pointer'
        }}
      >
        {productName}
      </button>

      {/* Price Input Form */}
      {showInput && (
        <form 
          onSubmit={handleSubmit} 
          style={{ 
            padding: '15px',
            borderTop: '1px solid #eee',
            backgroundColor: '#f8f9fa'
          }}
        >
          <div style={{ display: 'flex', gap: '8px' }}>
            <input
              type="text"
              value={price ? `$${price}` : ''}
              onChange={handlePriceChange}
              placeholder="Enter Price"
              required
              style={{ 
                flex: 1,
                padding: '12px',
                fontSize: '16px',
                textAlign: 'center',
                border: '1px solid #ddd',
                borderRadius: '6px',
                ':focus': {
                  outline: 'none',
                  borderColor: '#4CAF50'
                }
              }}
            />
            <button 
              type="submit" 
              style={{ 
                padding: '12px 20px',
                backgroundColor: '#4CAF50',
                color: 'white',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                fontSize: '16px',
                ':hover': {
                  backgroundColor: '#45a049'
                }
              }}
            >
              Add
            </button>
          </div>
        </form>
      )}
    </div>
  );
}

export default ProductBlock;
