import React from 'react';
import { Link, useLocation } from 'react-router-dom';

function Sidebar() {
  const location = useLocation();

  const menuItems = [
    { name: 'General', path: '/admin/sales' },
    { name: 'Gaudi', path: '/admin/sales/gaudi' },
    { name: 'Paralel', path: '/admin/sales/paralel' },
    { name: 'Mallorca', path: '/admin/sales/mallorca' },
    { name: 'Consell', path: '/admin/sales/consell' },
    { name: 'Hospital', path: '/admin/sales/hospital' }
  ];

  return (
    <div className="h-screen w-64 bg-gray-800 text-white fixed left-0 top-0">
      <div className="p-4">
        <h2 className="text-xl font-bold mb-6">POS Admin</h2>
        <nav>
          {menuItems.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              className={`block py-2 px-4 mb-2 rounded ${
                location.pathname === item.path
                  ? 'bg-blue-600'
                  : 'hover:bg-gray-700'
              }`}
            >
              {item.name}
            </Link>
          ))}
        </nav>
      </div>
    </div>
  );
}

export default Sidebar;
