import React, { useEffect, useState } from 'react';
import API from '../services/api';
import ProductBlock from './ProductBlock';
import Cart from './Cart';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await API.get('/api/products');
        console.log('Raw product data from API:', response.data.data);

        // Map the products to have consistent IDs
        const mappedProducts = response.data.data.map(product => ({
          ...product,
          id: parseInt(product.id) - 1  // Adjust ID to match Strapi admin
        }));

        console.log('Mapped products with adjusted IDs:', mappedProducts);
        setProducts(mappedProducts);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const addToCart = (product, price) => {
    console.log('Adding to cart with adjusted ID:', {
      originalId: product.id + 1,
      adjustedId: product.id,
      name: product.Product
    });
    setCartItems([...cartItems, { product, price }]);
  };

  const removeFromCart = (index) => {
    setCartItems(cartItems.filter((_, i) => i !== index));
  };

  const handleCheckout = async () => {
    try {
      console.log('Starting checkout with cart items:', cartItems);

      const salePromises = cartItems.map(async item => {
        // Adjust the ID back to match Strapi admin
        const saleData = {
          data: {
            Price: parseFloat(item.price),
            Time: new Date().toISOString(),
            store: parseInt(localStorage.getItem('storeId')),
            product: item.product.id + 1  // Adjust ID back for Strapi
          }
        };

        console.log('Creating sale with adjusted product ID:', {
          productName: item.product.Product,
          originalId: item.product.id,
          adjustedId: item.product.id + 1
        });

        const response = await API.post('/api/sales/create-with-relation', saleData);
        return response;
      });

      const responses = await Promise.all(salePromises);
      console.log('All sales completed:', responses.map(r => r.data));

      alert('Sales recorded successfully!');
      setCartItems([]);
    } catch (error) {
      console.error('Checkout error:', error);
      console.error('Error details:', error);
      alert('Error processing checkout');
    }
  };

  return (
    <div style={{ 
      display: 'flex', 
      height: '100vh',
      width: '100vw',
      overflow: 'hidden'
    }}>
      {/* Products Section - Left 50% */}
      <div style={{ 
        width: '50%',
        padding: '30px',
        overflowY: 'auto',
        borderRight: '2px solid #eee',
        backgroundColor: '#f8f9fa'
      }}>
        <h2 style={{
          fontSize: '24px',
          marginBottom: '25px',
          color: '#333'
        }}>Products</h2>
        <div style={{ 
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
          gap: '20px',
          padding: '10px'
        }}>
          {products.map((product) => (
            <ProductBlock 
              key={product.id} 
              product={product}
              onAddToCart={addToCart}
            />
          ))}
        </div>
      </div>

      {/* Cart Section - Right 50% */}
      <div style={{ 
        width: '50%',
        height: '100%'
      }}>
        <Cart 
          items={cartItems} 
          removeFromCart={removeFromCart}
          checkout={handleCheckout}
        />
      </div>
    </div>
  );
}

export default ProductList;
