import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../services/api';
import { generateInvoice } from '../utils/invoice';

function SalesDashboard() {
  const [sales, setSales] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const storeId = localStorage.getItem('storeId');
  const [selectedVat, setSelectedVat] = useState(0);

  useEffect(() => {
    const token = localStorage.getItem('jwtToken');
    if (!token) {
      navigate('/login');
      return;
    }
    const fetchSales = async () => {
      try {
        console.log('Fetching sales for store:', storeId);
        const response = await API.get('/api/sales', {
          params: {
            'filters[store][id][$eq]': storeId,
            'populate': {
              'invoice': true,
              'product': true,
              '*': true
            },
            'sort': 'createdAt:desc'
          }
        });

        const sortedSales = response.data.data.sort((a, b) => 
          new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
        );

        console.log('Sorted sales:', sortedSales);
        setSales(sortedSales);
      } catch (error) {
        console.error('Error fetching sales:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchSales();
  }, [storeId, navigate]);

  const handleDownloadInvoice = async (sale) => {
    // Find related sales either by orderGroupId or by time (within 1 second)
    let relatedSales;

    if (sale.orderGroupId) {
      // If orderGroupId exists, use it
      relatedSales = sales.filter(s => s.orderGroupId === sale.orderGroupId);
    } else {
      // Fallback to time-based grouping for older sales
      const saleTime = new Date(sale.Time).getTime();
      relatedSales = sales.filter(s => {
        const timeDiff = Math.abs(new Date(s.Time).getTime() - saleTime);
        return timeDiff < 1000;
      });
    }

    console.log('Related sales:', relatedSales);

    if (relatedSales.length === 0) {
      console.error('No related sales found for sale:', sale);
      return;
    }

    const items = relatedSales.map(s => ({
      product: { 
        Product: s.product?.Product || 'Producto no especificado'
      },
      price: s.Price,
      description: s.description || ''
    }));

    console.log('Items for invoice:', items);

    const total = relatedSales.reduce((sum, s) => sum + s.Price, 0);

    await generateInvoice(items, total, sale, selectedVat);
  };

  if (loading) return (
    <div style={{
      height: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: 'rgb(100, 116, 139)',
      fontSize: '14px',
      fontWeight: '500'
    }}>
      Loading sales data...
    </div>
  );

  return (
    <div style={{ 
      padding: '32px',
      maxWidth: '1400px',
      margin: '0 auto'
    }}>
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        marginBottom: '32px'
      }}>
        <h1 style={{
          fontSize: '24px',
          fontWeight: '600',
          color: 'rgb(15, 23, 42)',
          letterSpacing: '-0.01em',
          margin: 0
        }}>Sales History</h1>
        <button
          onClick={() => navigate('/pos')}
          onMouseEnter={(e) => {
            e.target.style.backgroundColor = 'rgb(241, 245, 249)';
          }}
          onMouseLeave={(e) => {
            e.target.style.backgroundColor = 'white';
          }}
          style={{
            padding: '10px 16px',
            backgroundColor: 'white',
            color: 'rgb(15, 23, 42)',
            border: '1px solid rgb(226, 232, 240)',
            borderRadius: '8px',
            cursor: 'pointer',
            fontSize: '14px',
            fontWeight: '500',
            transition: 'all 0.15s ease',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}
        >
          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M19 12H5M12 19l-7-7 7-7"/>
          </svg>
          Back to POS
        </button>
      </div>

      <div style={{
        backgroundColor: 'white',
        borderRadius: '12px',
        border: '1px solid rgb(226, 232, 240)',
        overflow: 'hidden'
      }}>
        <table style={{ 
          width: '100%', 
          borderCollapse: 'collapse'
        }}>
          <thead>
            <tr style={{
              backgroundColor: 'rgb(249, 250, 251)',
              borderBottom: '1px solid rgb(226, 232, 240)'
            }}>
              <th style={{ 
                padding: '12px 24px',
                textAlign: 'left',
                fontSize: '12px',
                fontWeight: '600',
                color: 'rgb(100, 116, 139)',
                textTransform: 'uppercase',
                letterSpacing: '0.05em'
              }}>Date</th>
              <th style={{ 
                padding: '12px 24px',
                textAlign: 'left',
                fontSize: '12px',
                fontWeight: '600',
                color: 'rgb(100, 116, 139)',
                textTransform: 'uppercase',
                letterSpacing: '0.05em'
              }}>Time</th>
              <th style={{ 
                padding: '12px 24px',
                textAlign: 'left',
                fontSize: '12px',
                fontWeight: '600',
                color: 'rgb(100, 116, 139)',
                textTransform: 'uppercase',
                letterSpacing: '0.05em'
              }}>Product</th>
              <th style={{ 
                padding: '12px 24px',
                textAlign: 'left',
                fontSize: '12px',
                fontWeight: '600',
                color: 'rgb(100, 116, 139)',
                textTransform: 'uppercase',
                letterSpacing: '0.05em'
              }}>Price</th>
              <th style={{ 
                padding: '12px 24px',
                textAlign: 'left',
                fontSize: '12px',
                fontWeight: '600',
                color: 'rgb(100, 116, 139)',
                textTransform: 'uppercase',
                letterSpacing: '0.05em'
              }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {sales && sales.length > 0 ? (
              sales.map((sale) => (
                <tr 
                  key={sale.id} 
                  style={{ 
                    borderBottom: '1px solid rgb(226, 232, 240)',
                    transition: 'background-color 0.15s ease'
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.backgroundColor = 'rgb(249, 250, 251)';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.backgroundColor = 'white';
                  }}
                >
                  <td style={{ padding: '16px 24px', fontSize: '14px', color: 'rgb(15, 23, 42)' }}>
                    {sale.Time ? new Date(sale.Time).toLocaleDateString('es-ES') : 'N/A'}
                  </td>
                  <td style={{ padding: '16px 24px', fontSize: '14px', color: 'rgb(15, 23, 42)' }}>
                    {sale.Time ? new Date(sale.Time).toLocaleTimeString('es-ES') : 'N/A'}
                  </td>
                  <td style={{ padding: '16px 24px', fontSize: '14px', color: 'rgb(15, 23, 42)' }}>
                    {sale.product?.Product || 'N/A'}
                  </td>
                  <td style={{ padding: '16px 24px', fontSize: '14px', fontWeight: '500', color: 'rgb(15, 23, 42)' }}>
                    â‚¬{sale.Price?.toFixed(2) || '0.00'}
                  </td>
                  <td style={{ padding: '16px 24px' }}>
                    <div style={{ 
                      display: 'flex', 
                      gap: '8px', 
                      alignItems: 'center'
                    }}>
                      <select
                        value={selectedVat}
                        onChange={(e) => setSelectedVat(Number(e.target.value))}
                        style={{
                          padding: '8px 12px',
                          borderRadius: '6px',
                          border: '1px solid rgb(226, 232, 240)',
                          fontSize: '14px',
                          color: 'rgb(15, 23, 42)',
                          backgroundColor: 'white',
                          cursor: 'pointer'
                        }}
                      >
                        <option value={0}>IVA: 0%</option>
                        <option value={10}>IVA: 10%</option>
                        <option value={21}>IVA: 21%</option>
                      </select>
                      <button
                        onClick={() => handleDownloadInvoice(sale)}
                        onMouseEnter={(e) => {
                          e.target.style.backgroundColor = 'rgb(29, 78, 216)';
                        }}
                        onMouseLeave={(e) => {
                          e.target.style.backgroundColor = 'rgb(37, 99, 235)';
                        }}
                        style={{
                          padding: '8px 16px',
                          backgroundColor: 'rgb(37, 99, 235)',
                          color: 'white',
                          border: 'none',
                          borderRadius: '6px',
                          cursor: 'pointer',
                          fontSize: '14px',
                          fontWeight: '500',
                          transition: 'all 0.15s ease'
                        }}
                      >
                        Download Invoice
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td 
                  colSpan="5" 
                  style={{ 
                    textAlign: 'center', 
                    padding: '32px',
                    color: 'rgb(100, 116, 139)',
                    fontSize: '14px'
                  }}
                >
                  No sales records found
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default SalesDashboard;
