import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../services/api';
import { generateInvoice } from '../utils/invoice';

function SalesDashboard() {
  const [salesGroups, setSalesGroups] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchSales = async () => {
      try {
        const response = await API.get('/api/sales?populate=*');

        // Group sales by orderGroupId
        const groups = response.data.data.reduce((acc, sale) => {
          const group = acc[sale.orderGroupId] || [];
          acc[sale.orderGroupId] = [...group, sale];
          return acc;
        }, {});

        setSalesGroups(Object.values(groups));
      } catch (error) {
        console.error('Error fetching sales:', error);
      }
    };

    fetchSales();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <h1>Sales History</h1>
        <button onClick={() => navigate('/pos')}>
          Back to POS
        </button>
      </div>

      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Date</th>
            <th>Time</th>
            <th>Items</th>
            <th>Total</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {salesGroups.map((group) => {
            const total = group.reduce((sum, sale) => sum + sale.Price, 0);
            const date = new Date(group[0].Time);

            return (
              <tr key={group[0].orderGroupId}>
                <td>{date.toLocaleDateString()}</td>
                <td>{date.toLocaleTimeString()}</td>
                <td>
                  {group.map(sale => sale.product.Product).join(', ')}
                </td>
                <td>${total.toFixed(2)}</td>
                <td>
                  <button 
                    onClick={() => generateInvoice(group, total)}
                    style={{
                      padding: '8px 12px',
                      backgroundColor: '#007bff',
                      color: 'white',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer'
                    }}
                  >
                    Download Invoice
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default SalesDashboard;
