import React, { useEffect, useState } from 'react';
import API from '../services/api';
import ProductBlock from './ProductBlock';
import Cart from './Cart';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await API.get('/api/products');
        if (!response.data.data || response.data.data.length === 0) {
          console.error('No products found in response');
          return;
        }
        setProducts(response.data.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const addToCart = (product, price) => {
    setCartItems([...cartItems, { product, price }]);
  };

  const removeFromCart = (index) => {
    setCartItems(cartItems.filter((_, i) => i !== index));
  };

  const handleCheckout = async () => {
    try {
      console.log('Starting checkout with cart items:', cartItems);

      const salePromises = cartItems.map(async item => {
        // Step 1: Create the sale first
        const saleData = {
          data: {
            Price: parseFloat(item.price),
            Time: new Date().toISOString(),
            store: parseInt(localStorage.getItem('storeId'))
          }
        };

        console.log('Creating initial sale with data:', saleData);
        const saleResponse = await API.post('/api/sales', saleData);
        const saleId = saleResponse.data.data.id;
        console.log('Sale created with ID:', saleId);

        // Step 2: Update the sale with the product relation
        const updateData = {
          data: {
            product: item.product.id
          }
        };

        console.log(`Updating sale ${saleId} with product:`, {
          productId: item.product.id,
          productName: item.product.Product
        });

        const updateResponse = await API.put(`/api/sales/${saleId}`, updateData);
        console.log('Update response:', updateResponse.data);

        return updateResponse;
      });

      const responses = await Promise.all(salePromises);
      console.log('All sales completed:', responses.map(r => r.data));

      alert('Sales recorded successfully!');
      setCartItems([]);
    } catch (error) {
      console.error('Error processing checkout:', error);
      alert('Error processing checkout');
    }
  };

  return (
    <div style={{ 
      display: 'flex', 
      height: '100vh',
      width: '100vw',
      overflow: 'hidden'
    }}>
      {/* Products Section - Left 50% */}
      <div style={{ 
        width: '50%',
        padding: '30px',
        overflowY: 'auto',
        borderRight: '2px solid #eee',
        backgroundColor: '#f8f9fa'
      }}>
        <h2 style={{
          fontSize: '24px',
          marginBottom: '25px',
          color: '#333'
        }}>Products</h2>
        <div style={{ 
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
          gap: '20px',
          padding: '10px'
        }}>
          {products.map((product) => (
            <ProductBlock 
              key={product.id} 
              product={product}
              onAddToCart={addToCart}
            />
          ))}
        </div>
      </div>

      {/* Cart Section - Right 50% */}
      <div style={{ 
        width: '50%',
        height: '100%'
      }}>
        <Cart 
          items={cartItems} 
          removeFromCart={removeFromCart}
          checkout={handleCheckout}
        />
      </div>
    </div>
  );
}

export default ProductList;
