import React, { useEffect, useState } from 'react';
import API from '../services/api';
import ProductBlock from './ProductBlock';
import Cart from './Cart';
import { useNavigate } from 'react-router-dom';
import SuccessModal from './SuccessModal';

const ID_MAP = {
  'Phone': 1,
  'Laptop': 3,
  'Funda': 5,
  'Charger': 7,
  'Figura': 9
};

// Modern color palette
const colors = {
  background: '#FAFAFA',
  surface: '#FFFFFF',
  primary: '#2563EB',
  text: {
    primary: '#1F2937',
    secondary: '#6B7280',
  },
  border: '#E5E7EB',
  hover: '#F3F4F6'
};

function ProductList() {
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await API.get('/api/products?populate=*');
        console.log('Raw product data from API:', response.data.data);

        // Map products to have correct IDs
        const correctedProducts = response.data.data.map(product => ({
          ...product,
          id: ID_MAP[product.Product] // Use the correct ID from our mapping
        }));

        console.log('Products with corrected IDs:', correctedProducts);
        setProducts(correctedProducts);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const addToCart = (product, price) => {
    console.log('Adding to cart:', {
      productName: product.Product,
      correctId: ID_MAP[product.Product],
      price
    });
    setCartItems([...cartItems, { product, price }]);
  };

  const removeFromCart = (index) => {
    setCartItems(cartItems.filter((_, i) => i !== index));
  };

  const handleCheckout = async () => {
    try {
      console.log('=== STARTING CHECKOUT ===');
      console.log('Cart items:', cartItems);

      for (const item of cartItems) {
        const saleData = {
          Price: parseFloat(item.price),
          Time: new Date().toISOString(),
          store: parseInt(localStorage.getItem('storeId')),
          product: item.product.id  // Make sure we're sending the product ID
        };

        console.log('=== CREATING SALE ===');
        console.log('Sale data:', saleData);

        const response = await API.post('/api/sales/create-with-relation', {
          data: saleData
        });

        console.log('=== SALE RESPONSE ===');
        console.log('Status:', response.status);
        console.log('Response data:', response.data);
      }

      setCartItems([]);
      navigate('/sales');
    } catch (error) {
      console.error('=== CHECKOUT ERROR ===');
      console.error('Error:', error);
      console.error('Response:', error.response?.data);
    }
  };

  return (
    <div style={{ 
      display: 'flex', 
      height: '100vh',
      width: '100vw',
      overflow: 'hidden',
      backgroundColor: colors.background,
      position: 'relative'
    }}>
      {/* Modern floating header */}
      <div style={{
        position: 'absolute',
        top: '20px',
        left: '20px',
        right: '20px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '16px 24px',
        backgroundColor: colors.surface,
        borderRadius: '16px',
        boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)',
        zIndex: 10
      }}>
        <h1 style={{
          fontSize: '24px',
          fontWeight: '600',
          color: colors.text.primary,
          margin: 0
        }}>
          POS System
        </h1>
        <button 
          onClick={() => navigate('/pos/sales')}
          style={{
            padding: '10px 16px',
            backgroundColor: 'rgba(37, 99, 235, 0.1)', // Light blue background with opacity
            color: '#2563eb', // Darker blue text
            border: '2px solid #2563eb', // Solid blue border
            borderRadius: '12px',
            cursor: 'pointer',
            fontSize: '14px',
            fontWeight: '500',
            transition: 'all 0.2s ease',
            display: 'flex',
            alignItems: 'center',
            gap: '6px',
            boxShadow: '0 1px 2px rgba(0, 0, 0, 0.05)',
            ':hover': {
              backgroundColor: 'rgba(37, 99, 235, 0.15)',
              transform: 'translateY(-1px)'
            }
          }}
        >
          <svg 
            width="16" 
            height="16" 
            viewBox="0 0 24 24" 
            fill="none" 
            stroke="currentColor" 
            strokeWidth="2" 
            strokeLinecap="round" 
            strokeLinejoin="round"
          >
            <path d="M3 3v18h18"/>
            <path d="m19 9-5 5-4-4-3 3"/>
          </svg>
          View Sales History
        </button>
      </div>

      {/* Products Section - Left side */}
      <div style={{ 
        width: '60%',
        padding: '90px 40px 40px 40px',
        overflowY: 'auto',
        backgroundColor: colors.background,
        borderRadius: '24px',
      }}>
        <div style={{ 
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(240px, 1fr))',
          gap: '24px',
          padding: '20px'
        }}>
          {products.map((product) => (
            <ProductBlock 
              key={product.id} 
              product={product}
              onAddToCart={addToCart}
            />
          ))}
        </div>
      </div>

      {/* Cart Section - Right side */}
      <div style={{ 
        width: '40%',
        height: '100%',
        backgroundColor: colors.surface,
        borderLeft: `1px solid ${colors.border}`,
        boxShadow: '-4px 0 15px rgba(0, 0, 0, 0.05)',
        display: 'flex',
        flexDirection: 'column'
      }}>
        <Cart 
          items={cartItems} 
          removeFromCart={removeFromCart}
          checkout={handleCheckout}
        />
      </div>

      {/* Success Modal */}
      {showSuccessModal && (
        <SuccessModal
          items={cartItems}
          total={cartItems.reduce((sum, item) => sum + parseFloat(item.price), 0)}
          onClose={() => {
            setShowSuccessModal(false);
            setCartItems([]);
          }}
        />
      )}
    </div>
  );
}

export default ProductList;
