import React, { useState } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Button,
  Box
} from '@mui/material';

function PriceModal({ open, onClose, onSubmit, product }) {
  const [price, setPrice] = useState('');
  const [description, setDescription] = useState('');

  const handleSubmit = () => {
    onSubmit({ price, description, product });
    setPrice('');
    setDescription('');
  };

  return (
    <Dialog 
      open={open} 
      onClose={onClose}
      PaperProps={{
        style: {
          borderRadius: '12px',
          padding: '6px'
        }
      }}
    >
      <DialogTitle sx={{
        fontSize: '18px',
        fontWeight: '600',
        color: 'rgb(15, 23, 42)',
        letterSpacing: '-0.01em',
        p: 3,
        pb: 2
      }}>
        {product?.Product}
      </DialogTitle>
      <DialogContent sx={{ p: 3, pt: 2 }}>
        <Box sx={{ 
          display: 'flex', 
          flexDirection: 'column', 
          gap: 2.5,
          minWidth: '400px'
        }}>
          <TextField
            autoFocus
            label="Price (â‚¬)"
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            inputProps={{ step: "0.01" }}
            sx={{
              '& .MuiOutlinedInput-root': {
                borderRadius: '8px',
                '& fieldset': {
                  borderColor: 'rgb(226, 232, 240)',
                },
                '&:hover fieldset': {
                  borderColor: 'rgb(148, 163, 184)',
                },
              }
            }}
          />
          <TextField
            label="Description"
            multiline
            rows={2}
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="e.g., iPhone 14 Pro Max 256GB"
            sx={{
              '& .MuiOutlinedInput-root': {
                borderRadius: '8px',
                '& fieldset': {
                  borderColor: 'rgb(226, 232, 240)',
                },
                '&:hover fieldset': {
                  borderColor: 'rgb(148, 163, 184)',
                },
              }
            }}
          />
        </Box>
      </DialogContent>
      <DialogActions sx={{ p: 3, pt: 2 }}>
        <Button 
          onClick={onClose}
          sx={{
            textTransform: 'none',
            fontWeight: '500',
            color: 'rgb(100, 116, 139)',
            '&:hover': {
              backgroundColor: 'rgb(241, 245, 249)',
            }
          }}
        >
          Cancel
        </Button>
        <Button 
          onClick={handleSubmit} 
          disabled={!price || !description}
          variant="contained"
          sx={{
            textTransform: 'none',
            fontWeight: '500',
            backgroundColor: 'rgb(37, 99, 235)',
            borderRadius: '6px',
            '&:hover': {
              backgroundColor: 'rgb(29, 78, 216)',
            },
            '&.Mui-disabled': {
              backgroundColor: 'rgb(226, 232, 240)',
              color: 'rgb(148, 163, 184)'
            }
          }}
        >
          Add to cart
        </Button>
      </DialogActions>
    </Dialog>
  );
}

export default PriceModal;
